INSERT INTO THUC_PHAM (TP_MA, DMDVT_MA, ND_MA, DMTP_MA, TP_TEN, TP_MO_TA, TP_HINH_ANH, TP_DON_GIA, TP_SO_LUONG, TP_NGAY_BAN, TP_VI_TRI_BAN_DO, TP_DIA_CHI, TP_SUAT_BAN) VALUES (1, 1, 1, 26, 'Bắp cải trắng – Loại 1kg', 'Cải trắng có vị ngọt, tính mát, có tác dụng hạ khí, thanh nhiệt, chứa nhiều vitamin A, B, C, E. Hàm lượng nguyên tố vi lượng kẽm cao hơn cả thịt, cá. Cải thảo nấu chín chứa nhiều vitamin A, C, K, B2, B6, calcium, sắt, mangan, folat, cũng như nhiều thành phần hoạt chất có ảnh hưởng tốt đối với sức khỏe. Cải thảo có thể dùng nấu canh ăn như các loại rau cải khác, cũng có thể ăn sống, muối chua, làm nộm như rau xà lách, nấu lẩu, xào… Khi chế biến cải thảo, bạn không nên nấu chín quá sẽ làm cải mất độ ngon, giòn và các vitamin dễ tan ở nhiệt độ cao.', 'static/images/67bee48f-5d67-4735-94db-f516e70c3502.jpg', 210000, 500, '2021-11-27', '10.03|105.77597', 'Ninh Kiều, Cần Thơ, Việt Nam', NULL);
INSERT INTO THUC_PHAM (TP_MA, DMDVT_MA, ND_MA, DMTP_MA, TP_TEN, TP_MO_TA, TP_HINH_ANH, TP_DON_GIA, TP_SO_LUONG, TP_NGAY_BAN, TP_VI_TRI_BAN_DO, TP_DIA_CHI, TP_SUAT_BAN) VALUES (2, 1, 2, 27, 'Dưa leo – Loại 1kg', '', 'static/images/9ffc7f5b-992e-4334-818a-ee1d2b06451f.jpg', 210000, 500, '2021-11-27', '10.0095|105.79059', 'Ninh Kiều, Cần Thơ, Việt Nam', NULL);
INSERT INTO THUC_PHAM (TP_MA, DMDVT_MA, ND_MA, DMTP_MA, TP_TEN, TP_MO_TA, TP_HINH_ANH, TP_DON_GIA, TP_SO_LUONG, TP_NGAY_BAN, TP_VI_TRI_BAN_DO, TP_DIA_CHI, TP_SUAT_BAN) VALUES (3, 1, 3, 27, 'Bí đỏ hồ lô – Loại 1kg', 'Bí đỏ là nguồn cung cấp dồi dào vitamin A, đóng vai trò quan trọng cho thị giác, tăng trưởng xương và sự sinh sản, điều hòa hệ miễn dịch và bảo vệ cho da. Bí đỏ được coi là món ăn bổ não, trị suy nhược thần kinh, trẻ em chậm phát triển về trí óc, và có tính nhuận trường. Ngoài tỷ lệ chất xơ và sắt khá cao, bí đỏ còn mang lại vitamin C, acid folic, magiê, kali và nhiều nguyên tố vi lượng khác. Đây cũng là thực phẩm cần cho những ai sợ mập vì dù có hàm lượng protein cao nhưng lại không chứa chất béo.', 'static/images/40f3b84f-e69e-46da-83e6-457cc960eace.jpg', 210000, 500, '2021-11-27', '10.03655|105.75801', 'Ninh Kiều, Cần Thơ, Việt Nam', NULL);
INSERT INTO THUC_PHAM (TP_MA, DMDVT_MA, ND_MA, DMTP_MA, TP_TEN, TP_MO_TA, TP_HINH_ANH, TP_DON_GIA, TP_SO_LUONG, TP_NGAY_BAN, TP_VI_TRI_BAN_DO, TP_DIA_CHI, TP_SUAT_BAN) VALUES (4, 1, 4, 36, 'Cam sành', '', 'static/images/11179659-5e2f-4aa7-adb3-613eb5d9a6ea.jpg', 295000, 500, '2021-11-27', '10.01091|105.78635', 'Ninh Kiều, Cần Thơ, Việt Nam', NULL);
INSERT INTO THUC_PHAM (TP_MA, DMDVT_MA, ND_MA, DMTP_MA, TP_TEN, TP_MO_TA, TP_HINH_ANH, TP_DON_GIA, TP_SO_LUONG, TP_NGAY_BAN, TP_VI_TRI_BAN_DO, TP_DIA_CHI, TP_SUAT_BAN) VALUES (5, 1, 5, 27, 'Cà chua – Loại 1kg', 'Cà chua là loại cà chua trái nhỏ, tròn hoặc dài, màu đỏ đều, đặc biệt có vị ngọt nhiều hơn cà chua thường. Với những món salad, bạn nên chọn cà chua bi để chế biến. Ngoài vị ngọt, cà chua bi còn rất giòn và tươi mát.', 'static/images/d7d2e270-a177-49f8-aba5-a2ffdb165fda.jpg', 255000, 500, '2021-11-27', '10.04939|105.78548', 'Ninh Kiều, Cần Thơ, Việt Nam', NULL);
INSERT INTO THUC_PHAM (TP_MA, DMDVT_MA, ND_MA, DMTP_MA, TP_TEN, TP_MO_TA, TP_HINH_ANH, TP_DON_GIA, TP_SO_LUONG, TP_NGAY_BAN, TP_VI_TRI_BAN_DO, TP_DIA_CHI, TP_SUAT_BAN) VALUES (6, 1, 6, 26, 'Rau muống lá – Loại 1kg', ' Theo y học cổ truyền, rau muống có tính mát, vị ngọt, giúp giải nhiệt, giải độc, nhuận tràng… Dưới đây là một số tác dụng cụ thể của món rau muống.', 'static/images/34da1aeb-8f20-45f5-9fd0-cc50c4e38f96.jpg', 210000, 500, '2021-11-27', '10.04611|105.72042', 'Ninh Kiều, Cần Thơ, Việt Nam', NULL);
INSERT INTO THUC_PHAM (TP_MA, DMDVT_MA, ND_MA, DMTP_MA, TP_TEN, TP_MO_TA, TP_HINH_ANH, TP_DON_GIA, TP_SO_LUONG, TP_NGAY_BAN, TP_VI_TRI_BAN_DO, TP_DIA_CHI, TP_SUAT_BAN) VALUES (7, 1, 7, 24, 'Khoai lang giống Nhật Đà Lạt – Loại 1kg', ' Khoai lang là món ăn phổ biến, rẻ tiền nhưng nó lại chứa nhiều dinh dưỡng tuyệt vời cho sức khỏe con người mà nhiều người không ngờ tới. Ăn khoai không những giúp cho bạn có làn da khỏe khoắn, hạn chế tình trạng khô da trong mùa đông mà giúp bạn có sức đề kháng trước các loại bệnh tật. Trong khoai lang chứa đến 40 % vitamin C, khoai lang cũng hàm chứa nhiều loại vitamin khác như vitamin D, magie, kali, sắt. Ngoài ra khoai lang là thực phẩm chứa nhiều chất xơ, carotene và chất chống oxy hóa. Một số món ngon chế biến từ khoai lang có thể kể đến là canh sườn non nấu khoai lang, xương hầm khoai lang, chè ngô khoai lang, cà ri nấu khoai lang…Ngoài ra món khoai lang luộc nóng hổi là một món ăn đơn giản nhưng không kém phần hấp dẫn.', 'static/images/5fdcc9e9-605c-4fa8-bbfd-33c29520bd67.jpg', 350000, 500, '2021-11-27', '10.0036|105.75167', 'Ninh Kiều, Cần Thơ, Việt Nam', NULL);
INSERT INTO THUC_PHAM (TP_MA, DMDVT_MA, ND_MA, DMTP_MA, TP_TEN, TP_MO_TA, TP_HINH_ANH, TP_DON_GIA, TP_SO_LUONG, TP_NGAY_BAN, TP_VI_TRI_BAN_DO, TP_DIA_CHI, TP_SUAT_BAN) VALUES (8, 1, 8, 24, 'Củ cải trắng – Loại 1kg', '', 'static/images/50d43c1a-1708-43d0-b0d5-b30bd9feef6f.jpg', 180000, 500, '2021-11-27', '10.0243|105.75543', 'Ninh Kiều, Cần Thơ, Việt Nam', NULL);
INSERT INTO THUC_PHAM (TP_MA, DMDVT_MA, ND_MA, DMTP_MA, TP_TEN, TP_MO_TA, TP_HINH_ANH, TP_DON_GIA, TP_SO_LUONG, TP_NGAY_BAN, TP_VI_TRI_BAN_DO, TP_DIA_CHI, TP_SUAT_BAN) VALUES (9, 1, 9, 36, 'Trái Ổi – Loại 1kg', '', 'static/images/a088d37c-fb12-44c2-b52b-68137347679e.jpg', 210000, 500, '2021-11-27', '10.04535|105.77541', 'Ninh Kiều, Cần Thơ, Việt Nam', NULL);
INSERT INTO THUC_PHAM (TP_MA, DMDVT_MA, ND_MA, DMTP_MA, TP_TEN, TP_MO_TA, TP_HINH_ANH, TP_DON_GIA, TP_SO_LUONG, TP_NGAY_BAN, TP_VI_TRI_BAN_DO, TP_DIA_CHI, TP_SUAT_BAN) VALUES (10, 1, 10, 26, 'Cải thảo – Loại 1kg', 'Cải thảo có vị ngọt, tính mát, có tác dụng hạ khí, thanh nhiệt, chứa nhiều vitamin A, B, C, E. Hàm lượng nguyên tố vi lượng kẽm cao hơn cả thịt, cá. Cải thảo nấu chín chứa nhiều vitamin A, C, K, B2, B6, calcium, sắt, mangan, folat, cũng như nhiều thành phần hoạt chất có ảnh hưởng tốt đối với sức khỏe. Cải thảo có thể dùng nấu canh ăn như các loại rau cải khác, cũng có thể ăn sống, muối chua, làm nộm như rau xà lách, nấu lẩu, xào… Khi chế biến cải thảo, bạn không nên nấu chín quá sẽ làm cải mất độ ngon, giòn và các vitamin dễ tan ở nhiệt độ cao.', 'static/images/9cb0ca30-4a9d-4ebd-acea-3fb2bfa0c4fd.jpg', 240000, 500, '2021-11-27', '10.0337|105.74555', 'Ninh Kiều, Cần Thơ, Việt Nam', NULL);
INSERT INTO THUC_PHAM (TP_MA, DMDVT_MA, ND_MA, DMTP_MA, TP_TEN, TP_MO_TA, TP_HINH_ANH, TP_DON_GIA, TP_SO_LUONG, TP_NGAY_BAN, TP_VI_TRI_BAN_DO, TP_DIA_CHI, TP_SUAT_BAN) VALUES (11, 1, 11, 24, 'Cà rốt Đà Lạt – Loại 1kg', 'Cà rốt Đà Lạt là một loại củ giàu về lượng đường, các loại vitamin cũng như năng lượng. Trong cà rốt có rất nhiều vitamin C, D, E và các vitamin nhóm B, ngoài ra còn chứa nhiều chất caroten. Cà rốt có tác dụng ngăn ngừa ung thư, đau tim, tai biến mạch máu não, tăng cường thị lực, giúp da mịn màng và căng tràn sức sống. Bên cạnh đó, cà rốt còn giúp cho hệ tiêu hóa khỏe mạnh, điều hòa huyết áp.', 'static/images/64b8e8c4-14b2-417f-80db-da66e65ee668.jpg', 280000, 500, '2021-11-27', '10.04605|105.76312', 'Ninh Kiều, Cần Thơ, Việt Nam', NULL);
INSERT INTO THUC_PHAM (TP_MA, DMDVT_MA, ND_MA, DMTP_MA, TP_TEN, TP_MO_TA, TP_HINH_ANH, TP_DON_GIA, TP_SO_LUONG, TP_NGAY_BAN, TP_VI_TRI_BAN_DO, TP_DIA_CHI, TP_SUAT_BAN) VALUES (12, 1, 12, 27, 'Bí xanh – Loại 1kg', 'Bí đao là loại thực phẩm chứa hàm lượng lớn các dưỡng chất thiết yếu cho cơ thể, giúp tăng cường sức khỏe và cải thiện nhan sắc cực kỳ hiệu quả, bởi trong bí đao chứa dồi dào chất xơ, các loại vitamin cần thiết như vitamin A, B, C và E cùng nhiều loại khoáng chất thiên nhiên khác…Vì vậy, bí đao rất được ưa chuộng và là nguyên liệu của nhiều loại món ăn.', 'static/images/f8df51dc-163c-4191-85be-56f5c7463f30.jpg', 180000, 500, '2021-11-27', '10.01953|105.77283', 'Ninh Kiều, Cần Thơ, Việt Nam', NULL);
INSERT INTO THUC_PHAM (TP_MA, DMDVT_MA, ND_MA, DMTP_MA, TP_TEN, TP_MO_TA, TP_HINH_ANH, TP_DON_GIA, TP_SO_LUONG, TP_NGAY_BAN, TP_VI_TRI_BAN_DO, TP_DIA_CHI, TP_SUAT_BAN) VALUES (13, 1, 13, 27, 'Chuối già hương – Loại 1kg', '', 'static/images/7dec14ab-d0d5-442a-8e32-e1f0e6d2781a.jpg', 170000, 500, '2021-11-27', '10.05038|105.77222', 'Ninh Kiều, Cần Thơ, Việt Nam', NULL);
INSERT INTO THUC_PHAM (TP_MA, DMDVT_MA, ND_MA, DMTP_MA, TP_TEN, TP_MO_TA, TP_HINH_ANH, TP_DON_GIA, TP_SO_LUONG, TP_NGAY_BAN, TP_VI_TRI_BAN_DO, TP_DIA_CHI, TP_SUAT_BAN) VALUES (14, 1, 14, 26, 'Cải ngọt – Loại 1kg', 'Cải ngọt là loài rau thuộc họ cải, rất dễ ăn và giàu chất dinh dưỡng. Trong 100 g cải có chứa: 1,1 g protein; 0,2 lipit; 2,1 g cacbohidrat; 61 mg canxi; 37 mg photpho; 0,5 mg sắt; 0,01 mg caroten; 0,02 thiamin (B1); 0,04 mg ribopalavin (B2); 0,3 mg niaxin (B3); 20 mg axit ascorbic (C). Theo Đông y, cải ngọt tính ôn, có công dụng thông lợi trường vị, làm đỡ tức ngực, tiêu thực hạ khí… có thể dùng để chữa các chứng ho, táo bón, ăn nhiều. Cải ngọt có thể chế biến thành các món ăn như cải ngọt xào thịt, canh cải ngọt nấu tôm, rau cải ngọt luộc chấm xì dầu, cải ngọt xào thịt bò, cải ngọt xào chân gà.', 'static/images/3ec4d167-8e48-43c2-9399-27f051435642.jpg', 210000, 500, '2021-11-27', '10.01953|105.77283', 'Ninh Kiều, Cần Thơ, Việt Nam', NULL);
INSERT INTO THUC_PHAM (TP_MA, DMDVT_MA, ND_MA, DMTP_MA, TP_TEN, TP_MO_TA, TP_HINH_ANH, TP_DON_GIA, TP_SO_LUONG, TP_NGAY_BAN, TP_VI_TRI_BAN_DO, TP_DIA_CHI, TP_SUAT_BAN) VALUES (15, 1, 15, 27, 'Khổ qua – Loại 1kg', 'Khổ qua (còn gọi là mướp đắng) có rất nhiều lợi ích cho sức khỏe. Khổ qua có vị đắng, tính hàn, công dụng thanh nhiệt, giải độc. Khổ qua thích hợp với các chứng sinh nhiệt, làm sáng mắt, mát tim, nhuận tràng. Hơn nữa, khổ qua còn có tác dụng giảm đường trong máu, chống sưng phù, điều trị nhiễm độc tố, thúc đẩy khả năng miễn dịch. Hạt khổ qua còn có tác dụng bổ thận tráng dương, trị chứng rôm sảy… Ngoài ra, khổ qua có tác dụng diệt vi khuẩn và virus, chống lại các tế bào ung thư làm chậm quá trình lão hóa và hạn chế nguy cơ phát sinh các bệnh tim mạch, tăng huyết áp, rối loạn lipid máu, tổn thương thần kinh, viêm đường tiết niệu, tiểu đường… Đặc biệt, khổ qua còn có hàm lượng vitamin C đứng hàng đầu trong các loại rau, gấp 5 – 20 lần dưa leo, bầu, bí… Khổ qua có thể chế biến thành nhiều món ăn ngon miệng, hấp dẫn và giàu dinh dưỡng như lẩu cá thác thác khổ qua, khổ qua nhồi thịt, trứng rán khổ qua, canh khổ qua thịt bằm, khổ qua xào trứng…', 'static/images/b1a708c4-0b2d-4396-98fd-a6c3822cf75c.jpg', 255000, 500, '2021-11-27', '9.99861|105.75364', 'Ninh Kiều, Cần Thơ, Việt Nam', NULL);
INSERT INTO THUC_PHAM (TP_MA, DMDVT_MA, ND_MA, DMTP_MA, TP_TEN, TP_MO_TA, TP_HINH_ANH, TP_DON_GIA, TP_SO_LUONG, TP_NGAY_BAN, TP_VI_TRI_BAN_DO, TP_DIA_CHI, TP_SUAT_BAN) VALUES (16, 1, 16, 27, 'Bầu dài – Loại 1kg', 'Quả bầu có vị ngọt, tính lạnh, có tác dụng giải nhiệt, giải độc, lợi tiểu, chữa đái dắt, đái đường… Hạt bầu còn có công dụng chữa lợi sưng đau, chân răng lở ngứa…Ngoài giá trị dinh dưỡng, bầu còn là một vị thuốc dân gian tốt có tác dụng thanh nhiệt, giải khát, nhuận tràng, chống táo bón. Ngay món bầu luộc chấm muối vừng tuy bình dị nhưng lại là một món ăn mát, bổ và lành. Sự kết hợp giữa bầu với vừng (nhất là vừng đen) làm cho món ăn trở thành ngon, bổ. Cả hai đều có tác dụng nhuận tràng, chống táo bón.', 'static/images/4b94bdee-3f69-4838-9c80-4f899a849b71.jpg', 180000, 500, '2021-11-27', '10.00857|105.78648', 'Ninh Kiều, Cần Thơ, Việt Nam', NULL);
INSERT INTO THUC_PHAM (TP_MA, DMDVT_MA, ND_MA, DMTP_MA, TP_TEN, TP_MO_TA, TP_HINH_ANH, TP_DON_GIA, TP_SO_LUONG, TP_NGAY_BAN, TP_VI_TRI_BAN_DO, TP_DIA_CHI, TP_SUAT_BAN) VALUES (17, 1, 17, 36, 'Dưa hấu – Loại 1kg', '', 'static/images/fee00f51-158d-40c3-905c-bd32188010e2.jpg', 210000, 500, '2021-11-27', '10.04098|105.75502', 'Ninh Kiều, Cần Thơ, Việt Nam', NULL);
INSERT INTO THUC_PHAM (TP_MA, DMDVT_MA, ND_MA, DMTP_MA, TP_TEN, TP_MO_TA, TP_HINH_ANH, TP_DON_GIA, TP_SO_LUONG, TP_NGAY_BAN, TP_VI_TRI_BAN_DO, TP_DIA_CHI, TP_SUAT_BAN) VALUES (18, 1, 18, 24, 'Hành tây – Loại 1kg', '', 'static/images/aea1b9b7-3907-4fec-becc-2d4459c884e1.jpg', 210000, 500, '2021-11-27', '10.00837|105.79014', 'Ninh Kiều, Cần Thơ, Việt Nam', NULL);
INSERT INTO THUC_PHAM (TP_MA, DMDVT_MA, ND_MA, DMTP_MA, TP_TEN, TP_MO_TA, TP_HINH_ANH, TP_DON_GIA, TP_SO_LUONG, TP_NGAY_BAN, TP_VI_TRI_BAN_DO, TP_DIA_CHI, TP_SUAT_BAN) VALUES (19, 2, 19, 25, 'Nấm Kim Châm – Gói 200g', '', 'static/images/6007eeb2-46b4-41db-8446-3800c71b3441.jpg', 170000, 500, '2021-11-27', '10.0226153|105.7627528', 'Ninh Kiều, Cần Thơ, Việt Nam', NULL);
INSERT INTO THUC_PHAM (TP_MA, DMDVT_MA, ND_MA, DMTP_MA, TP_TEN, TP_MO_TA, TP_HINH_ANH, TP_DON_GIA, TP_SO_LUONG, TP_NGAY_BAN, TP_VI_TRI_BAN_DO, TP_DIA_CHI, TP_SUAT_BAN) VALUES (20, 1, 20, 24, 'Su su – Loại 1kg', 'Trong quả su su có chứ nhiều folat, một loại vitamin B giúp ngăn chặn sự hình thành của Homocystein là chất có khả năng gây nên bệnh tim và đột quỵ. Do đó, nếu bạn muốn ngăn chặn căn bệnh hiểm nghèo trên hãy thường xuyên ăn quả su su nhé.', 'static/images/14f4bdae-2c60-4c10-824b-926d605bc30e.jpg', 180000, 500, '2021-11-27', '10.00837|105.79014', 'Ninh Kiều, Cần Thơ, Việt Nam', NULL);
INSERT INTO THUC_PHAM (TP_MA, DMDVT_MA, ND_MA, DMTP_MA, TP_TEN, TP_MO_TA, TP_HINH_ANH, TP_DON_GIA, TP_SO_LUONG, TP_NGAY_BAN, TP_VI_TRI_BAN_DO, TP_DIA_CHI, TP_SUAT_BAN) VALUES (21, 1, 21, 27, 'Mướp – Loại 1kg', 'Mướp là món ăn quen thuộc, thường dùng để nấu canh chung với rau đay, mồng tơi hay rau dền hoặc cho các món hấp, luộc, xào cũng rất mát và ngon miệng.', 'static/images/1e56ec02-fab6-4bd6-bc5e-e52b1600354e.jpg', 180000, 500, '2021-11-27', '10.01953|105.77283', 'Ninh Kiều, Cần Thơ, Việt Nam', NULL);
INSERT INTO THUC_PHAM (TP_MA, DMDVT_MA, ND_MA, DMTP_MA, TP_TEN, TP_MO_TA, TP_HINH_ANH, TP_DON_GIA, TP_SO_LUONG, TP_NGAY_BAN, TP_VI_TRI_BAN_DO, TP_DIA_CHI, TP_SUAT_BAN) VALUES (22, 1, 22, 26, 'Mồng tơi – Loại 1kg', 'Rau mồng tơi còn có các tên mùng tơi, tầm tơi, tên Hán là lạc quỳ, chung quỳ, yên chi thái, đằng thái. Theo Đông y, mồng tơi có vị chua ngọt, không độc, tính lạnh, có tác dụng giải độc, thanh nhiệt, hoạt tràng… Dùng mồng tơi chữa táo bón, đái dắt, kiết lỵ, làm đẹp da… vô cùng hiệu quả. Theo các nghiên cứu cho thấy, trong mồng tơi chứa chất nhầy pectin rất quý để phòng chữa nhiều bệnh, làm cho rau mồng tơi có tác dụng nhuận tràng, thải chất béo chống béo phì, thích hợp cho người có mỡ và đường cao trong máu. Tác dụng trừ thấp nhiệt, làm cho người lao động ngoài trời nắng nóng duy trì được sức khỏe, phòng chống bệnh tật như mỏi mệt háo khát, bứt rứt.', 'static/images/1395fc3a-99db-4675-be0c-cfafdb9520b9.jpg', 265000, 500, '2021-11-27', '10.04386|105.78016', 'Ninh Kiều, Cần Thơ, Việt Nam', NULL);
INSERT INTO THUC_PHAM (TP_MA, DMDVT_MA, ND_MA, DMTP_MA, TP_TEN, TP_MO_TA, TP_HINH_ANH, TP_DON_GIA, TP_SO_LUONG, TP_NGAY_BAN, TP_VI_TRI_BAN_DO, TP_DIA_CHI, TP_SUAT_BAN) VALUES (23, 1, 23, 24, 'Cà rốt – Loại 1kg', 'Cà rốt Đà Lạt là một loại củ giàu về lượng đường, các loại vitamin cũng như năng lượng. Trong cà rốt có rất nhiều vitamin C, D, E và các vitamin nhóm B, ngoài ra còn chứa nhiều chất caroten. Cà rốt có tác dụng ngăn ngừa ung thư, đau tim, tai biến mạch máu não, tăng cường thị lực, giúp da mịn màng và căng tràn sức sống. Bên cạnh đó, cà rốt còn giúp cho hệ tiêu hóa khỏe mạnh, điều hòa huyết áp.', 'static/images/c3226a84-3676-4f59-bf54-63eca36efef5.jpg', 210000, 500, '2021-11-27', '10.03931|105.75457', 'Ninh Kiều, Cần Thơ, Việt Nam', NULL);
