INSERT INTO THUC_PHAM (TP_MA, DMDVT_MA, ND_MA, DMTP_MA, TP_TEN, TP_MO_TA, TP_HINH_ANH, TP_DON_GIA, TP_SO_LUONG, TP_NGAY_BAN, TP_VI_TRI_BAN_DO, TP_DIA_CHI, TP_SUAT_BAN) VALUES (30, 2, 30, 16, 'Đậu xanh hột – Gói 150g', '', 'static/images/1a8f7d42-e881-4520-ab3f-4bcbe6a5c4d3.jpg', 105000, 500, '2021-11-27', '10.06377|105.75282', 'Ninh Kiều, Cần Thơ, Việt Nam', NULL);
INSERT INTO THUC_PHAM (TP_MA, DMDVT_MA, ND_MA, DMTP_MA, TP_TEN, TP_MO_TA, TP_HINH_ANH, TP_DON_GIA, TP_SO_LUONG, TP_NGAY_BAN, TP_VI_TRI_BAN_DO, TP_DIA_CHI, TP_SUAT_BAN) VALUES (31, 1, 31, 27, 'Cà tím – Loại 1kg', 'Cà tím Đà Lạt là loại rau có có nhiều dưỡng chất cần thiết cho cơ thể, giúp tăng cường sự dẻo dai cho các mạch máu và giảm bớt lượng cholesteral ngoài ra, giúp phòng ngừa bệnh cao huyết áp, xơ cứng động mạch. Lượng dinh dưỡng dồi dào trong cà tím có thể trị được các bệnh liên quan tới dạ dày như đau dạ dày, loét dạ dày, đau bụng do tiêu hóa, hay làm thuốc giảm đau, trị sưng khớp.', 'static/images/700f1669-f29b-40ef-8685-372d904688cb.jpg', 180000, 500, '2021-11-27', '10.02487|105.74707', 'Ninh Kiều, Cần Thơ, Việt Nam', NULL);
