INSERT INTO THUC_PHAM (TP_MA, DMDVT_MA, ND_MA, DMTP_MA, TP_TEN, TP_MO_TA, TP_HINH_ANH, TP_DON_GIA, TP_SO_LUONG, TP_NGAY_BAN, TP_VI_TRI_BAN_DO, TP_DIA_CHI, TP_SUAT_BAN) VALUES (35, 1, 35, 24, 'Khoai mỡ – Loại 1kg', '', 'static/images/f05612de-99cf-4395-a630-9f117a5e6f3b.jpg', 350000, 500, '2021-11-27', '10.01828|105.7428', 'Ninh Kiều, Cần Thơ, Việt Nam', NULL);
INSERT INTO THUC_PHAM (TP_MA, DMDVT_MA, ND_MA, DMTP_MA, TP_TEN, TP_MO_TA, TP_HINH_ANH, TP_DON_GIA, TP_SO_LUONG, TP_NGAY_BAN, TP_VI_TRI_BAN_DO, TP_DIA_CHI, TP_SUAT_BAN) VALUES (36, 1, 36, 24, 'Khoai tây vàng – Loại 1kg', 'Khoai tây vàng Đà Lạt là loại củ chứa nhiều chất dinh dưỡng, được nhiều người yêu thích. Đây là loại củ ít calo, không có chất béo và cholestrerol, hàm lượng vitamin cao và là nguồn cung cấp kali, vitamin B6 và chất xơ thô tuyệt vời. Các món ngon được chế biến từ khoai tây có thể kể đến là: Khoai tây xào thịt bò, khoai tây kẹp thịt hun khói nướng, khoai tây bỏ lò, khoai tây chiên, bánh khoai tây chiên xù…', 'static/images/5eeac265-4f13-4ac7-9887-61a3d5c366c8.jpg', 280000, 500, '2021-11-27', '10.0498|105.77568', 'Ninh Kiều, Cần Thơ, Việt Nam', NULL);
