INSERT INTO THUC_PHAM (TP_MA, DMDVT_MA, ND_MA, DMTP_MA, TP_TEN, TP_MO_TA, TP_HINH_ANH, TP_DON_GIA, TP_SO_LUONG, TP_NGAY_BAN, TP_VI_TRI_BAN_DO, TP_DIA_CHI, TP_SUAT_BAN) VALUES (39, 1, 39, 26, 'Cải dưa tươi (cải muối chua) – Loại 1kg', '', 'static/images/58c6e86c-7614-4c70-9d36-65441d3bb916.jpg', 240000, 500, '2021-11-27', '10.02954|105.75326', 'Ninh Kiều, Cần Thơ, Việt Nam', NULL);
