INSERT INTO THUC_PHAM (TP_MA, DMDVT_MA, ND_MA, DMTP_MA, TP_TEN, TP_MO_TA, TP_HINH_ANH, TP_DON_GIA, TP_SO_LUONG, TP_NGAY_BAN, TP_VI_TRI_BAN_DO, TP_DIA_CHI, TP_SUAT_BAN) VALUES (41, 1, 41, 24, 'Khoai lang tím – Loại 1kg', ' Khoai lang mật Đà Lạt có ruột màu vàng cam, vị ngọt dẻo rất thơm ngon, hấp dẫn. Trong củ khoai lang bao gồm một lượng lớn tinh bột, các acid amin, beta carotene, vitamin C, B1 và hơn 10 loại nguyên tố vi lượng cần thiết cho sức khỏe con người như canxi, phốt-pho, kẽm, sắt, magie, natri, kali,… Vì vậy, các chuyên gia dinh dưỡng đã gọi khoai lang là loại “thực phẩm cân bằng dinh dưỡng nhất”. Khoai lang mật Đà Lạt được chế biến thành nhiều món ăn ngon miệng như bánh nếp khoai lang mật, khoai lang mật nướng phô mai, khoai lang mật sấy giòn, mức khoai lang mật…Ngoài ra không thể không kể đến món khoai lang mật luộc với củ khoai nóng hổi, vàng ươm, thơm dẻo, tươm mật kích thích vị giác.', 'static/images/e1d776ac-76cb-4d66-9215-cab56bc1d1b5.jpg', 170000, 500, '2021-11-27', '10.03592|105.75738', 'Ninh Kiều, Cần Thơ, Việt Nam', NULL);
INSERT INTO THUC_PHAM (TP_MA, DMDVT_MA, ND_MA, DMTP_MA, TP_TEN, TP_MO_TA, TP_HINH_ANH, TP_DON_GIA, TP_SO_LUONG, TP_NGAY_BAN, TP_VI_TRI_BAN_DO, TP_DIA_CHI, TP_SUAT_BAN) VALUES (42, 1, 42, 25, 'Nấm đùi gà – Loại 1kg', '', 'static/images/0d54a9e6-edaf-4f32-81cd-3664c43f6b2d.jpg', 560000, 500, '2021-11-27', '10.03592|105.75738', 'Ninh Kiều, Cần Thơ, Việt Nam', NULL);
INSERT INTO THUC_PHAM (TP_MA, DMDVT_MA, ND_MA, DMTP_MA, TP_TEN, TP_MO_TA, TP_HINH_ANH, TP_DON_GIA, TP_SO_LUONG, TP_NGAY_BAN, TP_VI_TRI_BAN_DO, TP_DIA_CHI, TP_SUAT_BAN) VALUES (43, 1, 43, 26, 'Rau lang – Loại 1kg', ' Rau lang tính bình, vị ngọt, không độc, bổ hư tổn, ích khí lực, kiện tỳ vị, tư thận âm. Chữa tỳ hư, kém ăn, thận âm bất túc. Kiêng kỵ với các trường hợp thấp trệ, tiêu chảy, viêm dạ dày đa toan, đường huyết thấp.', 'static/images/8765f060-8625-486e-a23e-7ed0bb150d1b.jpg', 310000, 500, '2021-11-27', '9.98261|105.75423', 'Ninh Kiều, Cần Thơ, Việt Nam', NULL);
INSERT INTO THUC_PHAM (TP_MA, DMDVT_MA, ND_MA, DMTP_MA, TP_TEN, TP_MO_TA, TP_HINH_ANH, TP_DON_GIA, TP_SO_LUONG, TP_NGAY_BAN, TP_VI_TRI_BAN_DO, TP_DIA_CHI, TP_SUAT_BAN) VALUES (44, 2, 44, 25, 'Nấm đông cô tươi – Gói 200g', '', 'static/images/d226580d-9177-4865-b471-2c6e14447356.jpg', 350000, 500, '2021-11-27', '10.04611|105.72042', 'Ninh Kiều, Cần Thơ, Việt Nam', NULL);
INSERT INTO THUC_PHAM (TP_MA, DMDVT_MA, ND_MA, DMTP_MA, TP_TEN, TP_MO_TA, TP_HINH_ANH, TP_DON_GIA, TP_SO_LUONG, TP_NGAY_BAN, TP_VI_TRI_BAN_DO, TP_DIA_CHI, TP_SUAT_BAN) VALUES (45, 1, 45, 26, 'Cải bẹ xanh – Loại 1kg', 'Cải bẹ xanh (còn được gọi là cải cay) là một loại rau cung cấp nhiều dưỡng chất tốt cho cơ thể như các loại vitamin A, B, C, K , chất abumin , axit nicotic, caroten. Có rất nhiều món ăn ngon được chế biến từ rau cải bẹ xanh như cải bẹ xanh luộc, cải bẹ xanh nấu cá thác lác, cải bẹ xanh xào, gà hấp cải bẹ xanh…', 'static/images/ac5d79ef-b84f-408a-879d-0f57b11954a1.jpg', 265000, 500, '2021-11-27', '10.05026|105.72265', 'Ninh Kiều, Cần Thơ, Việt Nam', NULL);
INSERT INTO THUC_PHAM (TP_MA, DMDVT_MA, ND_MA, DMTP_MA, TP_TEN, TP_MO_TA, TP_HINH_ANH, TP_DON_GIA, TP_SO_LUONG, TP_NGAY_BAN, TP_VI_TRI_BAN_DO, TP_DIA_CHI, TP_SUAT_BAN) VALUES (46, 1, 46, 38, 'Bông cải xanh – Loại 1kg', 'Bông cải xanh Đà Lạt là cây thuộc loại cải, có hoa lớn ở đầu. Bông cải xanh cung cấp nhiều loại Vitamin cần thiết cho cơ thể như Vitamin A, C, E…giúp duy trì vẻ đẹp của làn da và tăng cường hệ miễn dịch cho cơ thể. Bông cải xanh Đà Lạt còn chứa nhiều chất xơ hỗ trợ tốt quá trình tiêu hóa và giảm nguy cơ tim mạch, tiểu đường. Ngoài ra, những chất khoáng khác tốt cho sức khỏe như canxi, kali, magie…cũng được tìm thấy nhiều trong bông cải xanh.', 'static/images/4495be8e-c9d7-4e80-a0e4-3e1bbd3c8b71.jpg', 630000, 500, '2021-11-27', '10.03592|105.75738', 'Ninh Kiều, Cần Thơ, Việt Nam', NULL);
INSERT INTO THUC_PHAM (TP_MA, DMDVT_MA, ND_MA, DMTP_MA, TP_TEN, TP_MO_TA, TP_HINH_ANH, TP_DON_GIA, TP_SO_LUONG, TP_NGAY_BAN, TP_VI_TRI_BAN_DO, TP_DIA_CHI, TP_SUAT_BAN) VALUES (47, 2, 47, 16, 'Đậu phộng sống – Gói 150g', '', 'static/images/f1554c79-9c6d-4e85-9399-5e0de00bd327.jpg', 150000, 500, '2021-11-27', '9.98261|105.75423', 'Ninh Kiều, Cần Thơ, Việt Nam', NULL);
INSERT INTO THUC_PHAM (TP_MA, DMDVT_MA, ND_MA, DMTP_MA, TP_TEN, TP_MO_TA, TP_HINH_ANH, TP_DON_GIA, TP_SO_LUONG, TP_NGAY_BAN, TP_VI_TRI_BAN_DO, TP_DIA_CHI, TP_SUAT_BAN) VALUES (48, 1, 48, 24, 'Khoai tây vàng Đà Lạt – Loại 1kg', ' Khoai tây vàng Đà Lạt là loại củ chứa nhiều chất dinh dưỡng, được nhiều người yêu thích. Đây là loại củ ít calo, không có chất béo và cholestrerol, hàm lượng vitamin cao và là nguồn cung cấp kali, vitamin B6 và chất xơ thô tuyệt vời. Các món ngon được chế biến từ khoai tây có thể kể đến là: Khoai tây xào thịt bò, khoai tây kẹp thịt hun khói nướng, khoai tây bỏ lò, khoai tây chiên, bánh khoai tây chiên xù…', 'static/images/d5d8b503-8b71-4fe1-896c-43772244e3c8.jpg', 350000, 500, '2021-11-27', '10.05164|105.77332', 'Ninh Kiều, Cần Thơ, Việt Nam', NULL);
INSERT INTO THUC_PHAM (TP_MA, DMDVT_MA, ND_MA, DMTP_MA, TP_TEN, TP_MO_TA, TP_HINH_ANH, TP_DON_GIA, TP_SO_LUONG, TP_NGAY_BAN, TP_VI_TRI_BAN_DO, TP_DIA_CHI, TP_SUAT_BAN) VALUES (49, 1, 49, 27, 'Chanh không hạt Đà Lạt – Loại 1kg', 'Chanh là loại quả có vị chua chứa nhiều vitamin C, nhiều nước và mùi thơm đặc biệt nên được sử dụng rỗng rãi hơn hết. Không chỉ có công dụng làm nước giải khát, làm chất phụ gia trong chế biến thực phẩm, là thành phần chế tạo các loại thuốc trong y học, chanh còn có nhiều công dụng bất ngờ trong đời sống hàng ngày như nhuận trường, dễ tiêu hóa, sát trùng, làm đẹp, tẩy mùi tanh của cá…', 'static/images/0cbc3071-bef8-4cf2-8f8d-d9a790ea15d3.jpg', 280000, 500, '2021-11-27', '10.05069|105.77477', 'Ninh Kiều, Cần Thơ, Việt Nam', NULL);
INSERT INTO THUC_PHAM (TP_MA, DMDVT_MA, ND_MA, DMTP_MA, TP_TEN, TP_MO_TA, TP_HINH_ANH, TP_DON_GIA, TP_SO_LUONG, TP_NGAY_BAN, TP_VI_TRI_BAN_DO, TP_DIA_CHI, TP_SUAT_BAN) VALUES (50, 1, 50, 26, 'Cần tây Đà Lạt – Loại 1kg', 'Rau cần có các thành phần: Canxi, Phốt pho, Sắt Vitamin P, C, Abumin, đường, Carôtin, Axit hữu cơ. Rau cần có vị thơm, tính bình, có tác dụng giải nhiệt cơ thể , bổ máu, thông đường ruột. Lượng chất sắt, phốt pho có trong rau cần nhiều giúp cho người sử dụng chữa nhiều bệnh tật: Thiếu máu, ho, viêm gan, viêm phế quản, tiểu đường, cao huyết áp… Rau cần nhiều chất xơ: Rau cần ít đường và không có chất béo nên hoàn toàn phù hợp với người muốn giảm cân. Chất xơ trong rau cần có tác dụng loại bỏ các chất thải độc hại trong hệ tiêu hoá. Đây là chất cần được nạp nhiều vào cơ thể nếu bạn muốn giảm trọng lượng. Bí đao, mướp đắng… cũng là những nguyên liệu chứa nhiều xơ, luôn được tin tưởng về hiệu quả giảm cân.', 'static/images/a1dff519-51c4-474b-9fd7-bf47e82d22a4.jpg', 490000, 500, '2021-11-27', '10.05164|105.77332', 'Ninh Kiều, Cần Thơ, Việt Nam', NULL);
INSERT INTO THUC_PHAM (TP_MA, DMDVT_MA, ND_MA, DMTP_MA, TP_TEN, TP_MO_TA, TP_HINH_ANH, TP_DON_GIA, TP_SO_LUONG, TP_NGAY_BAN, TP_VI_TRI_BAN_DO, TP_DIA_CHI, TP_SUAT_BAN) VALUES (51, 1, 51, 26, 'Cải ngồng – Loại 1kg', 'Cải ngòng là loài rau thuộc họ cải, rất dễ ăn và giàu chất dinh dưỡng. Trong 100 g cải có chứa: 1,1 g protein; 0,2 lipit; 2,1 g cacbohidrat; 61 mg canxi; 37 mg photpho; 0,5 mg sắt; 0,01 mg caroten; 0,02 thiamin (B1); 0,04 mg ribopalavin (B2); 0,3 mg niaxin (B3); 20 mg axit ascorbic (C). Theo Đông y, cải ngòng tính ôn, có công dụng thông lợi trường vị, làm đỡ tức ngực, tiêu thực hạ khí… có thể dùng để chữa các chứng ho, táo bón, ăn nhiều. Cải ngòng có thể chế biến thành các món ăn như cải ngòng xào thịt, canh cải ngòng nấu tôm, rau cải ngòng luộc chấm xì dầu, cải ngòng xào thịt bò, cải ngòng xào chân gà.', 'static/images/bdd9adaf-0d1f-49c5-ba9c-051d0e195481.jpg', 210000, 500, '2021-11-27', '10.25127|105.54782', 'Ninh Kiều, Cần Thơ, Việt Nam', NULL);
INSERT INTO THUC_PHAM (TP_MA, DMDVT_MA, ND_MA, DMTP_MA, TP_TEN, TP_MO_TA, TP_HINH_ANH, TP_DON_GIA, TP_SO_LUONG, TP_NGAY_BAN, TP_VI_TRI_BAN_DO, TP_DIA_CHI, TP_SUAT_BAN) VALUES (52, 2, 52, 25, 'Nấm linh chi trắng – Gói 150g', '', 'static/images/ef5a7e38-4bed-4d13-b85e-d6ca3f380a46.jpg', 170000, 500, '2021-11-27', '10.04605|105.76312', 'Ninh Kiều, Cần Thơ, Việt Nam', NULL);
INSERT INTO THUC_PHAM (TP_MA, DMDVT_MA, ND_MA, DMTP_MA, TP_TEN, TP_MO_TA, TP_HINH_ANH, TP_DON_GIA, TP_SO_LUONG, TP_NGAY_BAN, TP_VI_TRI_BAN_DO, TP_DIA_CHI, TP_SUAT_BAN) VALUES (53, 1, 53, 26, 'Rau dền – Loại 1kg', 'Rau dền chứa nhiều protit, lipit, glucit, nhiều vitamin và chất khoáng, vitamin, bêta-caroten, chứa nhiều chất xơ, có khả năng tăng cường miễn dịch, giúp giữ gìn sức khỏe. Rau dền không chứa axit oxalic, canxi và sắt trong dền sau khi đi vào cơ thể rất dễ được tận dụng và hấp thu, vì vậy, rau dền thúc đẩy phát triển cơ thể cho trẻ, có giá trị dinh dưỡng cao, giúp xương gãy mau liền.', 'static/images/80043efb-de9c-451c-8752-b9feaadfea4f.jpg', 265000, 500, '2021-11-27', '10.08298|105.73849', 'Ninh Kiều, Cần Thơ, Việt Nam', NULL);
INSERT INTO THUC_PHAM (TP_MA, DMDVT_MA, ND_MA, DMTP_MA, TP_TEN, TP_MO_TA, TP_HINH_ANH, TP_DON_GIA, TP_SO_LUONG, TP_NGAY_BAN, TP_VI_TRI_BAN_DO, TP_DIA_CHI, TP_SUAT_BAN) VALUES (54, 5, 54, 25, 'Nấm mối đen tươi đệ nhị – Gói 250g', 'Nấm mối đen tươi đệ nhất được nuôi trồng theo công nghệ khép kín 100% và chuẩn sạch hữu cơ. Nấm mối đen tươi được thu hoặc và xử lý bảo quản trong ngày để bảo đảm chất lượng được tốt nhất.Nấm mối đen có vị thanh ngọt, giòn, và chứa nhiều chất dinh dưỡng giá trị cao giúp: tăng cường sức đề kháng, ngăn ngừa ung thư, chống lão hóa, hỗ trợ tiêu hóa, giải độc và tăng cường chức năng gan…và còn nhiều chức năng nữa.Nấm mối đen tươi đệ nhất rất dễ chế biến và làm được với rất nhiều món ngon: súp nấm mối đen, trứng chiên nấm mối đen, bánh xèo chay nấm mối đen, nấm mối đen nướng, lẩu,…thậm chí là nấu mì gói với nấm mối đen tươi cũng rất ngon.Thực Phẩm Nhanh đang nuôi trồng và phân phối 3 loại nấm mối đen tươi tiêu chuẩn chất lượng cao bao gồm:– Nấm mối đen tươi đệ nhất: Là loại nấm mối được nuôi trồng khép kín, đạt chất lượng tốt nhất, kích thước đạt chuẩn lớn nhất và được cung cấp riêng cho các nhà hàng, khách sạn lớn tại TPHCM.– Nấm mối đen tươi đệ nhị: Loại nấm này cũng bảo đảm chất lượng 100% như nấm mối đen đệ nhất, tuy nhiên kích thước sẽ nhỏ hơn so với nấm mối đen đệ nhất.– Nấm mối đen tươi đệ tam: Nhằm mang đến những trải nghiệm sử dụng tốt nhất cho tất cả các phân khúc khách hàng của Thực Phẩm Nhanh.', 'static/images/94a99675-4895-4692-9bc9-3b849858114a.jpg', 720000, 500, '2021-11-27', '10.02329|105.78994', 'Ninh Kiều, Cần Thơ, Việt Nam', NULL);
