INSERT INTO THUC_PHAM (TP_MA, DMDVT_MA, ND_MA, DMTP_MA, TP_TEN, TP_MO_TA, TP_HINH_ANH, TP_DON_GIA, TP_SO_LUONG, TP_NGAY_BAN, TP_VI_TRI_BAN_DO, TP_DIA_CHI, TP_SUAT_BAN) VALUES (60, 1, 60, 27, 'Đậu que – Loại 1kg', '', 'static/images/230db0d6-6601-4f2c-9985-259ffa386dfe.jpg', 280000, 500, '2021-11-27', '10.03655|105.75801', 'Ninh Kiều, Cần Thơ, Việt Nam', NULL);
INSERT INTO THUC_PHAM (TP_MA, DMDVT_MA, ND_MA, DMTP_MA, TP_TEN, TP_MO_TA, TP_HINH_ANH, TP_DON_GIA, TP_SO_LUONG, TP_NGAY_BAN, TP_VI_TRI_BAN_DO, TP_DIA_CHI, TP_SUAT_BAN) VALUES (61, 1, 61, 26, 'Sả cây – Loại 1kg', 'Với hương và mùi vị khá nồng, phần thân sả thường được sử dụng trong chế biến món ăn. Phần ngọn sả nhiều xơ tuy không dùng nấu ăn nhưng có thể ép lấy nước hoặc pha trà.', 'static/images/5c808a0f-bea5-4adc-aa16-96a45ad3c641.jpg', 180000, 500, '2021-11-27', '10.0446704|105.7745184', 'Ninh Kiều, Cần Thơ, Việt Nam', NULL);
INSERT INTO THUC_PHAM (TP_MA, DMDVT_MA, ND_MA, DMTP_MA, TP_TEN, TP_MO_TA, TP_HINH_ANH, TP_DON_GIA, TP_SO_LUONG, TP_NGAY_BAN, TP_VI_TRI_BAN_DO, TP_DIA_CHI, TP_SUAT_BAN) VALUES (62, 1, 62, 26, 'Rau ngót – Loại 1kg', ' Theo Đông y, rau ngót tính mát lạnh (nấu chín sẽ bớt lạnh), vị ngọt. Có công năng thanh nhiệt, giải độc, lợi tiểu, tăng tiết nước bọt, hoạt huyết hoá ứ, bổ huyết, cầm huyết, nhuận tràng, sát khuẩn, tiêu viêm, sinh cơ, có nhiều tác dụng chữa bệnh.', 'static/images/03d7052a-5dad-4886-801d-4dc22033a47b.jpg', 280000, 500, '2021-11-27', '10.10018|105.71179', 'Ninh Kiều, Cần Thơ, Việt Nam', NULL);
INSERT INTO THUC_PHAM (TP_MA, DMDVT_MA, ND_MA, DMTP_MA, TP_TEN, TP_MO_TA, TP_HINH_ANH, TP_DON_GIA, TP_SO_LUONG, TP_NGAY_BAN, TP_VI_TRI_BAN_DO, TP_DIA_CHI, TP_SUAT_BAN) VALUES (63, 1, 63, 26, 'Xà lách xoong lá lớn – Loại 1kg', ' Xà lách chứa rất nhiều muối khoáng với các nguyên tố kiềm, nhờ đó giúp cơ thể “dọn dẹp” máu, mang lại sự tỉnh táo tinh thần và tránh được nhiều bệnh tật.', 'static/images/95ec1dca-6fa3-40dc-ab9e-b1b5a274bb12.jpg', 210000, 500, '2021-11-27', '10.00152|105.75238', 'Ninh Kiều, Cần Thơ, Việt Nam', NULL);
