INSERT INTO THUC_PHAM (TP_MA, DMDVT_MA, ND_MA, DMTP_MA, TP_TEN, TP_MO_TA, TP_HINH_ANH, TP_DON_GIA, TP_SO_LUONG, TP_NGAY_BAN, TP_VI_TRI_BAN_DO, TP_DIA_CHI, TP_SUAT_BAN) VALUES (69, 1, 69, 27, 'Bí ngòi xanh Đà Lạt – Loại 1kg', 'Bí ngòi là nhà máy tổng hợp viamin C, vitamin A, các nguyên tố khoáng vi lượng như magnesium, manganese, potassium (kali), đồng, chất xơ, chứa nhiều vitamin nhóm B (B1, B2, B3, B6)…Bí ngòi được dùng trong bữa ăn dành cho bệnh nhân hen suyễn do có nhiều vitamin C. Ăn bí ngòi thường xuyên sẽ làm hạ nồng độ homocysteine và hỗ trợ cấu trúc các mao mạch. Sự tăng hàm lượng homocysteine thường là những nguyên nhân gây nhồi máu cơ tim và đột quỵ. Chất phenol có trong quả bí ngòi có tác dụng giúp cơ thể phòng chống lại các bệnh tim mạch, một số bệnh ung thư và bệnh mãn tính. Sở dĩ chất phenol có những tác dụng như trên vì nhờ đặc tính chống ôxy hóa rất cao của nó. Ngoài ra, chất bioflavonoitbí ngòi còn rất tốt cho mắt, chất này bảo vệ mắt, giúp mắt luôn khỏe và sáng.', 'static/images/a17e7307-274a-4cec-9ee2-072c62c33699.jpg', 350000, 500, '2021-11-27', '10.03592|105.75738', 'Ninh Kiều, Cần Thơ, Việt Nam', NULL);
