INSERT INTO THUC_PHAM (TP_MA, DMDVT_MA, ND_MA, DMTP_MA, TP_TEN, TP_MO_TA, TP_HINH_ANH, TP_DON_GIA, TP_SO_LUONG, TP_NGAY_BAN, TP_VI_TRI_BAN_DO, TP_DIA_CHI, TP_SUAT_BAN) VALUES (73, 1, 73, 26, 'Cải dúng Đà Lạt – Loại 1kg', 'Cải dún Đà Lạt là một loại rau cung cấp nhiều vitamin C và nhiều nguyên tố vi lượng cần thiết cho sự phát triển của cơ thể. Cải dún có vị dễ ăn, có tác dụng làm mát gan, thanh lọc, giải nhiệt cơ thể khi nấu canh. Ngoài ra có rất nhiều món ăn ngon khác được chế biến từ rau cải dún như cải luộc chấm nước mắm trứng, cải xào nấm, canh cải dún nấu thịt…', 'static/images/be47c503-2efe-4de4-a731-7e339c4525b6.jpg', 210000, 500, '2021-11-27', '10.05164|105.77332', 'Ninh Kiều, Cần Thơ, Việt Nam', NULL);
