INSERT INTO THUC_PHAM (TP_MA, DMDVT_MA, ND_MA, DMTP_MA, TP_TEN, TP_MO_TA, TP_HINH_ANH, TP_DON_GIA, TP_SO_LUONG, TP_NGAY_BAN, TP_VI_TRI_BAN_DO, TP_DIA_CHI, TP_SUAT_BAN) VALUES (75, 1, 75, 27, 'Bắp nếp – Loại 1kg', '', 'static/images/0d14f982-81e7-4a51-8a26-7fcf29a3fbaa.jpg', 350000, 500, '2021-11-27', '10.05164|105.77332', 'Ninh Kiều, Cần Thơ, Việt Nam', NULL);
INSERT INTO THUC_PHAM (TP_MA, DMDVT_MA, ND_MA, DMTP_MA, TP_TEN, TP_MO_TA, TP_HINH_ANH, TP_DON_GIA, TP_SO_LUONG, TP_NGAY_BAN, TP_VI_TRI_BAN_DO, TP_DIA_CHI, TP_SUAT_BAN) VALUES (76, 1, 76, 24, 'Khoai sọ – Loại 1kg', 'Khoai môn sáp Đà Lạt bùi, thơm ngon, có vị mát, giúp giải nhiệt cơ thể rất tốt. Ngoài ra, nguồn giá trị dinh dưỡng cao trong khoai còn giúp người ăn bồi bổ cơ thể tránh khỏi bệnh tật. Khoai môn cung cấy đầy đủ các chất đạm, tinh bột, các loại vitamin A, C, B… giúp cơ thể con người chống lại các chất gây lão hóa da, gia tăng thị lực, tăng cường sức đề kháng, nhuận tràng… Khoai môn sáp được sử dụng để nấu nhiều món ăn thơm ngon, hấp dẫn và giàu dinh dưỡng như chè khoai môn, bánh khoai môn sáp chiên giòn, khoai môn, canh khoai môn giò heo, khoai môn chiên cốm, cháo hạt sen khoai môn, khoai môn xào…', 'static/images/1cbd2923-297e-4049-8691-a4baae597440.jpg', 280000, 500, '2021-11-27', '10.05164|105.77332', 'Ninh Kiều, Cần Thơ, Việt Nam', NULL);
INSERT INTO THUC_PHAM (TP_MA, DMDVT_MA, ND_MA, DMTP_MA, TP_TEN, TP_MO_TA, TP_HINH_ANH, TP_DON_GIA, TP_SO_LUONG, TP_NGAY_BAN, TP_VI_TRI_BAN_DO, TP_DIA_CHI, TP_SUAT_BAN) VALUES (77, 2, 77, 16, 'Đậu đen xanh lòng – Gói 300g', '', 'static/images/376669dd-7e9e-4401-961d-f7c1eb7abd9b.jpg', 280000, 500, '2021-11-27', '10.05164|105.77332', 'Ninh Kiều, Cần Thơ, Việt Nam', NULL);
INSERT INTO THUC_PHAM (TP_MA, DMDVT_MA, ND_MA, DMTP_MA, TP_TEN, TP_MO_TA, TP_HINH_ANH, TP_DON_GIA, TP_SO_LUONG, TP_NGAY_BAN, TP_VI_TRI_BAN_DO, TP_DIA_CHI, TP_SUAT_BAN) VALUES (78, 5, 78, 25, 'Nấm mối đen tươi đệ tam – Gói 250g', 'Nấm mối đen tươi đệ nhất được nuôi trồng theo công nghệ khép kín 100% và chuẩn sạch hữu cơ. Nấm mối đen tươi được thu hoặc và xử lý bảo quản trong ngày để bảo đảm chất lượng được tốt nhất.Nấm mối đen có vị thanh ngọt, giòn, và chứa nhiều chất dinh dưỡng giá trị cao giúp: tăng cường sức đề kháng, ngăn ngừa ung thư, chống lão hóa, hỗ trợ tiêu hóa, giải độc và tăng cường chức năng gan…và còn nhiều chức năng nữa.Nấm mối đen tươi đệ nhất rất dễ chế biến và làm được với rất nhiều món ngon: súp nấm mối đen, trứng chiên nấm mối đen, bánh xèo chay nấm mối đen, nấm mối đen nướng, lẩu,…thậm chí là nấu mì gói với nấm mối đen tươi cũng rất ngon.Thực Phẩm Nhanh đang nuôi trồng và phân phối 3 loại nấm mối đen tươi tiêu chuẩn chất lượng cao bao gồm:– Nấm mối đen tươi đệ nhất: Là loại nấm mối được nuôi trồng khép kín, đạt chất lượng tốt nhất, kích thước đạt chuẩn lớn nhất và được cung cấp riêng cho các nhà hàng, khách sạn lớn tại TPHCM.– Nấm mối đen tươi đệ nhị: Loại nấm này cũng bảo đảm chất lượng 100% như nấm mối đen đệ nhất, tuy nhiên kích thước sẽ nhỏ hơn so với nấm mối đen đệ nhất.– Nấm mối đen tươi đệ tam: Nhằm mang đến những trải nghiệm sử dụng tốt nhất cho tất cả các phân khúc khách hàng của Thực Phẩm Nhanh.', 'static/images/28b6aa91-eac6-4650-9089-24345ef5d923.jpg', 610000, 500, '2021-11-27', '10.0354938|105.7716347', 'Ninh Kiều, Cần Thơ, Việt Nam', NULL);
