INSERT INTO THUC_PHAM (TP_MA, DMDVT_MA, ND_MA, DMTP_MA, TP_TEN, TP_MO_TA, TP_HINH_ANH, TP_DON_GIA, TP_SO_LUONG, TP_NGAY_BAN, TP_VI_TRI_BAN_DO, TP_DIA_CHI, TP_SUAT_BAN) VALUES (83, 1, 83, 27, 'Bí đỏ lớn – Loại 1kg', 'Bí đỏ là nguồn cung cấp dồi dào vitamin A, đóng vai trò quan trọng cho thị giác, tăng trưởng xương và sự sinh sản, điều hòa hệ miễn dịch và bảo vệ cho da. Bí đỏ được coi là món ăn bổ não, trị suy nhược thần kinh, trẻ em chậm phát triển về trí óc, và có tính nhuận trường. Ngoài tỷ lệ chất xơ và sắt khá cao, bí đỏ còn mang lại vitamin C, acid folic, magiê, kali và nhiều nguyên tố vi lượng khác. Đây cũng là thực phẩm cần cho những ai sợ mập vì dù có hàm lượng protein cao nhưng lại không chứa chất béo.', 'static/images/fa94a2c7-dc84-4ca8-bb69-c821d8c97e90.jpg', 255000, 500, '2021-11-27', '10.03466|105.77934', 'Ninh Kiều, Cần Thơ, Việt Nam', NULL);
