INSERT INTO THUC_PHAM (TP_MA, DMDVT_MA, ND_MA, DMTP_MA, TP_TEN, TP_MO_TA, TP_HINH_ANH, TP_DON_GIA, TP_SO_LUONG, TP_NGAY_BAN, TP_VI_TRI_BAN_DO, TP_DIA_CHI, TP_SUAT_BAN) VALUES (100, 1, 100, 26, 'Bắp cải tím – Loại 1kg', 'Cải tím có vị ngọt, tính mát, có tác dụng hạ khí, thanh nhiệt, chứa nhiều vitamin A, B, C, E. Hàm lượng nguyên tố vi lượng kẽm cao hơn cả thịt, cá. Cải thảo nấu chín chứa nhiều vitamin A, C, K, B2, B6, calcium, sắt, mangan, folat, cũng như nhiều thành phần hoạt chất có ảnh hưởng tốt đối với sức khỏe. Cải thảo có thể dùng nấu canh ăn như các loại rau cải khác, cũng có thể ăn sống, muối chua, làm nộm như rau xà lách, nấu lẩu, xào… Khi chế biến cải thảo, bạn không nên nấu chín quá sẽ làm cải mất độ ngon, giòn và các vitamin dễ tan ở nhiệt độ cao.', 'static/images/6a44d7d4-3331-44aa-b101-856d28b0788c.jpg', 350000, 500, '2021-11-27', '10.037|105.75415', 'Ninh Kiều, Cần Thơ, Việt Nam', NULL);
