INSERT INTO THUC_PHAM (TP_MA, DMDVT_MA, ND_MA, DMTP_MA, TP_TEN, TP_MO_TA, TP_HINH_ANH, TP_DON_GIA, TP_SO_LUONG, TP_NGAY_BAN, TP_VI_TRI_BAN_DO, TP_DIA_CHI, TP_SUAT_BAN) VALUES (105, 5, 5, 25, 'Nấm mối đen tươi đệ nhất – Gói 250g', 'Nấm mối đen tươi đệ nhất được nuôi trồng theo công nghệ khép kín 100% và chuẩn sạch hữu cơ. Nấm mối đen tươi được thu hoặc và xử lý bảo quản trong ngày để bảo đảm chất lượng được tốt nhất.Nấm mối đen có vị thanh ngọt, giòn, và chứa nhiều chất dinh dưỡng giá trị cao giúp: tăng cường sức đề kháng, ngăn ngừa ung thư, chống lão hóa, hỗ trợ tiêu hóa, giải độc và tăng cường chức năng gan…và còn nhiều chức năng nữa.Nấm mối đen tươi đệ nhất rất dễ chế biến và làm được với rất nhiều món ngon: súp nấm mối đen, trứng chiên nấm mối đen, bánh xèo chay nấm mối đen, nấm mối đen nướng, lẩu,…thậm chí là nấu mì gói với nấm mối đen tươi cũng rất ngon.Thực Phẩm Nhanh đang nuôi trồng và phân phối 3 loại nấm mối đen tươi tiêu chuẩn chất lượng cao bao gồm:– Nấm mối đen tươi đệ nhất: Là loại nấm mối được nuôi trồng khép kín, đạt chất lượng tốt nhất, kích thước đạt chuẩn lớn nhất và được cung cấp riêng cho các nhà hàng, khách sạn lớn tại TPHCM.– Nấm mối đen tươi đệ nhị: Loại nấm này cũng bảo đảm chất lượng 100% như nấm mối đen đệ nhất, tuy nhiên kích thước sẽ nhỏ hơn so với nấm mối đen đệ nhất.– Nấm mối đen tươi đệ tam: Nhằm mang đến những trải nghiệm sử dụng tốt nhất cho tất cả các phân khúc khách hàng của Thực Phẩm Nhanh.', 'static/images/da8518ab-9908-47c5-a475-1645ef2577e8.jpg', 800000, 500, '2021-11-27', '10.02364|105.79084', 'Ninh Kiều, Cần Thơ, Việt Nam', NULL);
