INSERT INTO THUC_PHAM (TP_MA, DMDVT_MA, ND_MA, DMTP_MA, TP_TEN, TP_MO_TA, TP_HINH_ANH, TP_DON_GIA, TP_SO_LUONG, TP_NGAY_BAN, TP_VI_TRI_BAN_DO, TP_DIA_CHI, TP_SUAT_BAN) VALUES (109, 1, 9, 36, 'Thanh long – Loại 1kg', '', 'static/images/41288828-70e7-4549-9c64-a703526b859e.jpg', 350000, 500, '2021-11-27', '10.01953|105.77283', 'Ninh Kiều, Cần Thơ, Việt Nam', NULL);
INSERT INTO THUC_PHAM (TP_MA, DMDVT_MA, ND_MA, DMTP_MA, TP_TEN, TP_MO_TA, TP_HINH_ANH, TP_DON_GIA, TP_SO_LUONG, TP_NGAY_BAN, TP_VI_TRI_BAN_DO, TP_DIA_CHI, TP_SUAT_BAN) VALUES (110, 1, 10, 27, 'Ớt chuông xanh – Loại 1kg', ' Ớt chuông là loại quả có lượng vitamin C kỉ lục. Cứ 100 g ớt có chứa 120 mg vitamin C. Lượng vitamin C này gấp 2,5 lần so với cam. Thực tế, chỉ cần 50g ớt tây đã chứa 60g vitamin C tương đương với 75% lượng vitamin C cơ thể cần mỗi ngày. Ngoài vitamin C, ớt tây cũng rất giàu vitamin A. Cứ 100g ớt tây có chứa khoảng 3,5mg vitamin A, có thể cung cấp từ 15 đến 50 % lượng vitamin A cần thiết trong ngày. Ớt chuông vàng và xanh tham gia vào quá trình collagen, giúp da đàn hồi, nuôi dưỡng gia rất hiệu quả. Công thức làm 1 mặt nạ ớt chuông rất đơn giản, bạn chỉ cần xay nhuyễn 1 trái ớt chuông vàng hoặc xanh và đắp hỗn hợp đó lên mặt khoảng 15 phút rồi rửa mặt sạch với nước lạnh. Mặt nạ này sẽ giúp tuần hoàn máu, cung cấp độ đàn hồi, trả lại sự trẻ trung mịn màng cho làn da khô ráp của bạn. Ngoài ra, ớt chuông còn có tác dụng giảm cân, ngăn ngừa lão hóa, trị nám, tàn nhang…', 'static/images/47e51236-a0f6-42b1-a43b-559fcd4d3a96.jpg', 560000, 500, '2021-11-27', '10.0095|105.79059', 'Ninh Kiều, Cần Thơ, Việt Nam', NULL);
