INSERT INTO THUC_PHAM (TP_MA, DMDVT_MA, ND_MA, DMTP_MA, TP_TEN, TP_MO_TA, TP_HINH_ANH, TP_DON_GIA, TP_SO_LUONG, TP_NGAY_BAN, TP_VI_TRI_BAN_DO, TP_DIA_CHI, TP_SUAT_BAN) VALUES (114, 1, 14, 24, 'Khoai tây bi Đà Lạt – Loại 1kg', ' Khoai tây bi Đà Lạt là loại củ chứa nhiều chất dinh dưỡng, được nhiều người yêu thích. Đây là loại củ ít calo, không có chất béo và cholestrerol, hàm lượng vitamin cao và là nguồn cung cấp kali, vitamin B6 và chất xơ thô tuyệt vời. Các món ngon được chế biến từ khoai tây có thể kể đến là: khoai tây bi om mặn, khoai tây bi nấu nấm, khoai tây bi sốt cà, thịt ba chỉ kho khoai tây bi, sò điệp nấu khoai tây bi…', 'static/images/9a662ae3-8dba-4f67-b00a-a9d5e3075dd9.jpg', 350000, 500, '2021-11-27', '10.03592|105.75738', 'Ninh Kiều, Cần Thơ, Việt Nam', NULL);
INSERT INTO THUC_PHAM (TP_MA, DMDVT_MA, ND_MA, DMTP_MA, TP_TEN, TP_MO_TA, TP_HINH_ANH, TP_DON_GIA, TP_SO_LUONG, TP_NGAY_BAN, TP_VI_TRI_BAN_DO, TP_DIA_CHI, TP_SUAT_BAN) VALUES (115, 1, 15, 27, 'Cà chua bi – Loại 1kg', 'Cà chua baby Đà Lạt (hay cà chua bi) là loại cà chua trái nhỏ, tròn hoặc dài, màu đỏ đều, đặc biệt có vị ngọt nhiều hơn cà chua thường. Với những món salad, bạn nên chọn cà chua bi để chế biến. Ngoài vị ngọt, cà chua bi còn rất giòn và tươi mát.', 'static/images/675d9d11-10ba-4a8a-b1b0-48995c88c039.jpg', 280000, 500, '2021-11-27', '10.03251|105.7808', 'Ninh Kiều, Cần Thơ, Việt Nam', NULL);
