INSERT INTO THUC_PHAM (TP_MA, DMDVT_MA, ND_MA, DMTP_MA, TP_TEN, TP_MO_TA, TP_HINH_ANH, TP_DON_GIA, TP_SO_LUONG, TP_NGAY_BAN, TP_VI_TRI_BAN_DO, TP_DIA_CHI, TP_SUAT_BAN) VALUES (134, 1, 34, 26, 'Bạc hà – Loại 1kg', 'Bạc hà tốt cho hệ tiêu hóa và chữa trị các bệnh đường ruột, chữa các bệnh cảm sốt, giảm căng thẳng và mất ngủ, tốt cho răng và giúp lưu thông máu, tăng cường hệ miễn dịch, giảm đau, làm đẹp da, có tác dụng trị gàu và được sử dụng làm dược liệu để chữa trị các chứng đau dạ dày và đau tức ngực. Trà bạc hà có công dụng lợi tiểu mạnh và trợ giúp tiêu hóa, bằng cách tiêu hủy mỡ. Trong những năm gần đây, lá bạc hà còn được sử dụng trong việc điều trị chứng béo phì. ', 'static/images/b2f324f0-0ad1-4427-9625-5a3ea0e2f9a8.jpg', 210000, 500, '2021-11-27', '10.03626|105.78457', 'Ninh Kiều, Cần Thơ, Việt Nam', NULL);
