INSERT INTO THUC_PHAM (TP_MA, DMDVT_MA, ND_MA, DMTP_MA, TP_TEN, TP_MO_TA, TP_HINH_ANH, TP_DON_GIA, TP_SO_LUONG, TP_NGAY_BAN, TP_VI_TRI_BAN_DO, TP_DIA_CHI, TP_SUAT_BAN) VALUES (139, 1, 39, 26, 'Lá Giang – Loại 1kg', ' Lá giang là một loại lá có vị chua thường mọc ở nhiều vùng đồi núi và thường dùng để nấu canh. Những món ăn nấu từ lá giang thường rất đậm đà, thơm ngon và cực kỳ tốt cho sức khỏe. Lá giang còn có thể dùng làm rau sống, đọt và lá giang non được dùng để ăn sống hay bóp gỏi. Ngoài ra, lá giang dùng để xào với thịt trâu, bò, gà, vịt… Các món ăn phổ biến được làm từ lá giang có thể kể đến là canh chua gà nấu lá giang, lẩu cá kèo lá giang, bò xào lá giang…', 'static/images/96159044-7b19-4f29-906a-1326d332bb6e.jpg', 420000, 500, '2021-11-27', '10.00829|105.79368', 'Ninh Kiều, Cần Thơ, Việt Nam', NULL);
