INSERT INTO THUC_PHAM (TP_MA, DMDVT_MA, ND_MA, DMTP_MA, TP_TEN, TP_MO_TA, TP_HINH_ANH, TP_DON_GIA, TP_SO_LUONG, TP_NGAY_BAN, TP_VI_TRI_BAN_DO, TP_DIA_CHI, TP_SUAT_BAN) VALUES (144, 1, 44, 26, 'Xà lách xoong lá nhỏ – Loại 1kg', ' Xà lách chứa rất nhiều muối khoáng với các nguyên tố kiềm, nhờ đó giúp cơ thể “dọn dẹp” máu, mang lại sự tỉnh táo tinh thần và tránh được nhiều bệnh tật.', 'static/images/30b66eb3-13b2-4ed3-9d07-d3956283c31e.jpg', 700000, 500, '2021-11-27', '10.00837|105.79014', 'Ninh Kiều, Cần Thơ, Việt Nam', NULL);
INSERT INTO THUC_PHAM (TP_MA, DMDVT_MA, ND_MA, DMTP_MA, TP_TEN, TP_MO_TA, TP_HINH_ANH, TP_DON_GIA, TP_SO_LUONG, TP_NGAY_BAN, TP_VI_TRI_BAN_DO, TP_DIA_CHI, TP_SUAT_BAN) VALUES (145, 1, 45, 38, 'Sả xay – Loại 1kg', ' Với hương và mùi vị khá nồng, phần thân sả thường được sử dụng trong chế biến món ăn. Phần ngọn sả nhiều xơ tuy không dùng nấu ăn nhưng có thể ép lấy nước hoặc pha trà.', 'static/images/b73ed128-e8ad-460f-bb53-827be32c46e9.jpg', 140000, 500, '2021-11-27', '10.04878|105.77466', 'Ninh Kiều, Cần Thơ, Việt Nam', NULL);
INSERT INTO THUC_PHAM (TP_MA, DMDVT_MA, ND_MA, DMTP_MA, TP_TEN, TP_MO_TA, TP_HINH_ANH, TP_DON_GIA, TP_SO_LUONG, TP_NGAY_BAN, TP_VI_TRI_BAN_DO, TP_DIA_CHI, TP_SUAT_BAN) VALUES (146, 1, 46, 26, 'Rau muống bào – Loại 1kg', ' Theo y học cổ truyền, rau muống có tính mát, vị ngọt, giúp giải nhiệt, giải độc, nhuận tràng… Dưới đây là một số tác dụng cụ thể của món rau muống.', 'static/images/82b4a4e1-9e73-43b1-9132-947be85b391c.jpg', 210000, 500, '2021-11-27', '10.0401|105.77955', 'Ninh Kiều, Cần Thơ, Việt Nam', NULL);
