INSERT INTO THUC_PHAM (TP_MA, DMDVT_MA, ND_MA, DMTP_MA, TP_TEN, TP_MO_TA, TP_HINH_ANH, TP_DON_GIA, TP_SO_LUONG, TP_NGAY_BAN, TP_VI_TRI_BAN_DO, TP_DIA_CHI, TP_SUAT_BAN) VALUES (148, 1, 48, 25, 'Nấm rơm – Loại 1kg', '', 'static/images/84a60c13-33e3-4eaf-b118-48f8461c4214.jpg', 045000, 500, '2021-11-27', '10.0484279|105.7655999', 'Ninh Kiều, Cần Thơ, Việt Nam', NULL);
