INSERT INTO THUC_PHAM (TP_MA, DMDVT_MA, ND_MA, DMTP_MA, TP_TEN, TP_MO_TA, TP_HINH_ANH, TP_DON_GIA, TP_SO_LUONG, TP_NGAY_BAN, TP_VI_TRI_BAN_DO, TP_DIA_CHI, TP_SUAT_BAN) VALUES (150, 1, 50, 24, 'Khoai môn cao – Loại 1kg', 'Khoai môn sáp Đà Lạt bùi, thơm ngon, có vị mát, giúp giải nhiệt cơ thể rất tốt. Ngoài ra, nguồn giá trị dinh dưỡng cao trong khoai còn giúp người ăn bồi bổ cơ thể tránh khỏi bệnh tật. Khoai môn cung cấy đầy đủ các chất đạm, tinh bột, các loại vitamin A, C, B… giúp cơ thể con người chống lại các chất gây lão hóa da, gia tăng thị lực, tăng cường sức đề kháng, nhuận tràng… Khoai môn sáp được sử dụng để nấu nhiều món ăn thơm ngon, hấp dẫn và giàu dinh dưỡng như chè khoai môn, bánh khoai môn sáp chiên giòn, khoai môn, canh khoai môn giò heo, khoai môn chiên cốm, cháo hạt sen khoai môn, khoai môn xào…', 'static/images/1a87cf1c-ac46-4513-8010-06f9aa017d3e.jpg', 350000, 500, '2021-11-27', '10.02909|105.78533', 'Ninh Kiều, Cần Thơ, Việt Nam', NULL);
