INSERT INTO THUC_PHAM (TP_MA, DMDVT_MA, ND_MA, DMTP_MA, TP_TEN, TP_MO_TA, TP_HINH_ANH, TP_DON_GIA, TP_SO_LUONG, TP_NGAY_BAN, TP_VI_TRI_BAN_DO, TP_DIA_CHI, TP_SUAT_BAN) VALUES (191, 1, 91, 24, 'Gừng non – Loại 1kg', ' Gừng là một loại gia vị nấu ăn quen thuộc và hầu như không thể thiếu trong gian bếp của người nội trợ. Nó không chỉ giảm bớt mùi của thực phẩm, làm tăng thêm hương vị đậm đà cho món ăn mà còn giảm bớt nhiều thành phần có hại tiềm tàng trong thực phẩm, giúp cơ thể dễ dàng tiêu hóa và hấp thụ thức ăn. Gừng chứa đựng cả hai giá trị dinh dưỡng và y tế, vừa là thuốc vừa là nguyên liệu cho nhiều món ăn ngon miệng hơn. Một số món ăn thơm ngon, hấp dẫn được chế biến từ gừng như mứt gừng, gà kho gừng, gà hấp gừng, canh cải nấu gừng, bò kho gừng…', 'static/images/7efac05e-5c66-474d-a69d-da96461266ce.jpg', 560000, 500, '2021-11-27', '10.00021|105.74297', 'Ninh Kiều, Cần Thơ, Việt Nam', NULL);
