INSERT INTO THUC_PHAM (TP_MA, DMDVT_MA, ND_MA, DMTP_MA, TP_TEN, TP_MO_TA, TP_HINH_ANH, TP_DON_GIA, TP_SO_LUONG, TP_NGAY_BAN, TP_VI_TRI_BAN_DO, TP_DIA_CHI, TP_SUAT_BAN) VALUES (215, 1, 15, 26, 'Rau má lá nhỏ – Loại 1kg', 'Rau má có tác dụng trấn tĩnh, an thần thông qua cơ chế tác động trực tiếp lên hệ thống thần kinh trung ương. Điều trị các vết ở da và niêm mạc là do các saponin chứa trong dịch chiết có tác dụng kích thích quá trình sinh trưởng của tế bào da, làm tăng sinh mạng lưới huyết quản của tổ chức liên kết giúp cho các mô tái tạo nhanh chóng khiến vết thương mau lành.', 'static/images/73fb6f0e-8afb-4d79-b96c-03c0a4c88f99.jpg', 310000, 500, '2021-11-27', '10.01998|105.75118', 'Ninh Kiều, Cần Thơ, Việt Nam', NULL);
INSERT INTO THUC_PHAM (TP_MA, DMDVT_MA, ND_MA, DMTP_MA, TP_TEN, TP_MO_TA, TP_HINH_ANH, TP_DON_GIA, TP_SO_LUONG, TP_NGAY_BAN, TP_VI_TRI_BAN_DO, TP_DIA_CHI, TP_SUAT_BAN) VALUES (216, 1, 16, 26, 'Rau đay – Loại 1kg', ' Rau đay loại rau có nhiều muối khoáng và vitamin. Các nhà nghiên cứu đã phân tích thành phần hoá học của rau đay thấy có canxi 498mg%, photpho 93mg%, sắt 3,8mg%, kali 650mg%, axit oxalic 870mg%, vitamin B1 0,24mg%, vitamin B2 0,76mg%, vitamin C 168mg%, vitamin A 7,940 đơn vị…', 'static/images/70c6fcc2-7dc2-4e21-9da0-d847ee80fb4c.jpg', 350000, 500, '2021-11-27', '16.57933|107.36759', 'Ninh Kiều, Cần Thơ, Việt Nam', NULL);
INSERT INTO THUC_PHAM (TP_MA, DMDVT_MA, ND_MA, DMTP_MA, TP_TEN, TP_MO_TA, TP_HINH_ANH, TP_DON_GIA, TP_SO_LUONG, TP_NGAY_BAN, TP_VI_TRI_BAN_DO, TP_DIA_CHI, TP_SUAT_BAN) VALUES (217, 1, 17, 27, 'Ớt xiêm xanh – Loại 1kg', ' Theo y học cổ truyền, ớt có vị cay, nóng. Tác dụng khoan trung, tán hàn, kiện tỳ, tiêu thực, chỉ thống (giảm đau), kháng nham (chữa ung thư…), thường dùng để chữa đau bụng do lạnh, tiêu hóa kém, đau khớp, dùng ngoài chữa rắn rết cắn… Ngoài dùng làm thuốc, ớt còn được dùng để nấu canh ăn.', 'static/images/a7642bf5-8ee8-494e-aef7-4bacacdca716.jpg', 845000, 500, '2021-11-27', '10.04626|105.76777', 'Ninh Kiều, Cần Thơ, Việt Nam', NULL);
INSERT INTO THUC_PHAM (TP_MA, DMDVT_MA, ND_MA, DMTP_MA, TP_TEN, TP_MO_TA, TP_HINH_ANH, TP_DON_GIA, TP_SO_LUONG, TP_NGAY_BAN, TP_VI_TRI_BAN_DO, TP_DIA_CHI, TP_SUAT_BAN) VALUES (218, 1, 18, 27, 'Ớt sừng đỏ – Loại 1kg', ' Theo y học cổ truyền, ớt có vị cay, nóng. Tác dụng khoan trung, tán hàn, kiện tỳ, tiêu thực, chỉ thống (giảm đau), kháng nham (chữa ung thư…), thường dùng để chữa đau bụng do lạnh, tiêu hóa kém, đau khớp, dùng ngoài chữa rắn rết cắn… Ngoài dùng làm thuốc, ớt còn được dùng để nấu canh ăn.', 'static/images/f53c9a31-e17f-49b4-863a-6bf9a826af6d.jpg', 560000, 500, '2021-11-27', '10.01828|105.7428', 'Ninh Kiều, Cần Thơ, Việt Nam', NULL);
INSERT INTO THUC_PHAM (TP_MA, DMDVT_MA, ND_MA, DMTP_MA, TP_TEN, TP_MO_TA, TP_HINH_ANH, TP_DON_GIA, TP_SO_LUONG, TP_NGAY_BAN, TP_VI_TRI_BAN_DO, TP_DIA_CHI, TP_SUAT_BAN) VALUES (219, 1, 19, 27, 'Ớt chuông vàng Đà Lạt – Loại 1kg', ' Ớt chuông là loại quả có lượng vitamin C kỉ lục. Cứ 100 g ớt có chứa 120 mg vitamin C. Lượng vitamin C này gấp 2,5 lần so với cam. Thực tế, chỉ cần 50g ớt tây đã chứa 60g vitamin C tương đương với 75% lượng vitamin C cơ thể cần mỗi ngày. Ngoài vitamin C, ớt tây cũng rất giàu vitamin A. Cứ 100g ớt tây có chứa khoảng 3,5mg vitamin A, có thể cung cấp từ 15 đến 50 % lượng vitamin A cần thiết trong ngày. Ớt chuông vàng và xanh tham gia vào quá trình collagen, giúp da đàn hồi, nuôi dưỡng gia rất hiệu quả. Công thức làm 1 mặt nạ ớt chuông rất đơn giản, bạn chỉ cần xay nhuyễn 1 trái ớt chuông vàng hoặc xanh và đắp hỗn hợp đó lên mặt khoảng 15 phút rồi rửa mặt sạch với nước lạnh. Mặt nạ này sẽ giúp tuần hoàn máu, cung cấp độ đàn hồi, trả lại sự trẻ trung mịn màng cho làn da khô ráp của bạn. Ngoài ra, ớt chuông còn có tác dụng giảm cân, ngăn ngừa lão hóa, trị nám, tàn nhang…', 'static/images/6d03aabd-05ff-405e-8043-f3b8c9457971.jpg', 775000, 500, '2021-11-27', '10.03226|105.77408', 'Ninh Kiều, Cần Thơ, Việt Nam', NULL);
