INSERT INTO THUC_PHAM (TP_MA, DMDVT_MA, ND_MA, DMTP_MA, TP_TEN, TP_MO_TA, TP_HINH_ANH, TP_DON_GIA, TP_SO_LUONG, TP_NGAY_BAN, TP_VI_TRI_BAN_DO, TP_DIA_CHI, TP_SUAT_BAN) VALUES (226, 1, 26, 36, 'Dâu tây Đà Lạt – Loại 1kg', ' Dâu tây là một nguồn vitamin C tuyệt vời, một nguồn mangan dồi dào, nguồn chất xơ, iốt rất tốt cho cơ thể và có lợi cho hệ tiêu hóa, giúp giảm cholesterol. Ngoài ra, dâu tây chứa nhiều vitamin C chống lão hóa hơn cả cam và bưởi giúp bổ sung nhiều dưỡng chất cho da, chống lão hóa, xóa mờ các vết nám đen, nứt nẻ và da khô, mang lại làn da hồng hào cho bạn. Nguồn vitamin C dồi dào ở dâu tây còn giúp bạn tẩy trắng cả hàm răng một cách nhẹ nhàng và lấy đi những vết ố vàng trên răng. Ngoài ra, dâu tây có chứa các chất dinh dưỡng thiết yếu sau đây: Kali, Folate, Sinh tố B, A xít Omega-3, Vitamin K, Magiê, Đồng…giúp làn da trở nên căng mịn và đầy sức sống. Một cốc dâu tây có rất ít chất béo (chỉ 0,6 gam chất béo) và 46 calo. Bên cạnh đó dâu tây cũng chứa phytonutrients – một chất quan trọng để bảo vệ cơ thể khỏi các căn bệnh nguy hiểm khác.', 'static/images/837a3892-ade6-4dec-b633-4c06c4211128.jpg', 211330, 500, '2021-11-27', '10.04939|105.78548', 'Ninh Kiều, Cần Thơ, Việt Nam', NULL);
