INSERT INTO THUC_PHAM (TP_MA, DMDVT_MA, ND_MA, DMTP_MA, TP_TEN, TP_MO_TA, TP_HINH_ANH, TP_DON_GIA, TP_SO_LUONG, TP_NGAY_BAN, TP_VI_TRI_BAN_DO, TP_DIA_CHI, TP_SUAT_BAN) VALUES (284, 1, 84, 26, 'Rau đắng – Loại 1kg', 'Rau đắng có vị đắng, tính bình, không độc, có tác dụng lợi tiêu hóa, khai vị, lợi tiểu, trừ thấp nhiệt, sơ can. Những trường hợp có thể sử dụng để trị bệnh như: tiểu gắt buốt, sỏi thận, ăn uống kém tiêu, nóng trong người, hạ sốt, mát gan.', 'static/images/ba658126-bf4b-4c47-b387-b5d589438fc5.jpg', 211330, 500, '2021-11-27', '10.0280134|105.7765082', 'Ninh Kiều, Cần Thơ, Việt Nam', NULL);
INSERT INTO THUC_PHAM (TP_MA, DMDVT_MA, ND_MA, DMTP_MA, TP_TEN, TP_MO_TA, TP_HINH_ANH, TP_DON_GIA, TP_SO_LUONG, TP_NGAY_BAN, TP_VI_TRI_BAN_DO, TP_DIA_CHI, TP_SUAT_BAN) VALUES (285, 1, 85, 26, 'Thì là – Loại 1kg', ' Rau bó xôi chứa đến 35 vitamin và khoáng chất thiết yếu. Nó rất giàu vitamin C và A, nhờ đó làm tăng sức đề kháng cho cơ thể, khiến bạn ít ốm đau hơn, lại làm đẹp da, sáng mắt. Chất luteun trong rau bó xôi cũng giúp ngăn ngừa thoái hóa điểm đen và đục thủy tinh thể. Lượng chất sắt và phốt pho dồi dào có tác dụng bổ máu. Chất beta carotene, canxi, magie và vitamin K cũng có nhiều trong loại rau này tốt cho xương và răng. Vitamin K kích hoạt osteocalcin, loại protein quan trọng trong xương. Xương bạn sẽ yếu đi nếu thiếu loại vitamin này.', 'static/images/80aa2831-55b9-46a7-a5de-6411a98b5c2c.jpg', 405000, 500, '2021-11-27', '10.0259722|105.7823721', 'Ninh Kiều, Cần Thơ, Việt Nam', NULL);
INSERT INTO THUC_PHAM (TP_MA, DMDVT_MA, ND_MA, DMTP_MA, TP_TEN, TP_MO_TA, TP_HINH_ANH, TP_DON_GIA, TP_SO_LUONG, TP_NGAY_BAN, TP_VI_TRI_BAN_DO, TP_DIA_CHI, TP_SUAT_BAN) VALUES (286, 1, 86, 38, 'Ớt xay – Loại 1kg', ' Theo y học cổ truyền, ớt có vị cay, nóng. Tác dụng khoan trung, tán hàn, kiện tỳ, tiêu thực, chỉ thống (giảm đau), kháng nham (chữa ung thư…), thường dùng để chữa đau bụng do lạnh, tiêu hóa kém, đau khớp, dùng ngoài chữa rắn rết cắn… Ngoài dùng làm thuốc, ớt còn được dùng để nấu canh ăn.', 'static/images/60bac474-bdec-4b77-99bf-381101cb0b8a.jpg', 490000, 500, '2021-11-27', '10.00476|105.80472', 'Ninh Kiều, Cần Thơ, Việt Nam', NULL);
INSERT INTO THUC_PHAM (TP_MA, DMDVT_MA, ND_MA, DMTP_MA, TP_TEN, TP_MO_TA, TP_HINH_ANH, TP_DON_GIA, TP_SO_LUONG, TP_NGAY_BAN, TP_VI_TRI_BAN_DO, TP_DIA_CHI, TP_SUAT_BAN) VALUES (287, 1, 87, 27, 'Ớt sừng xanh – Loại 1kg', ' Theo y học cổ truyền, ớt có vị cay, nóng. Tác dụng khoan trung, tán hàn, kiện tỳ, tiêu thực, chỉ thống (giảm đau), kháng nham (chữa ung thư…), thường dùng để chữa đau bụng do lạnh, tiêu hóa kém, đau khớp, dùng ngoài chữa rắn rết cắn… Ngoài dùng làm thuốc, ớt còn được dùng để nấu canh ăn.', 'static/images/74640071-b5db-40c0-bebe-dbd49ad61058.jpg', 700000, 500, '2021-11-27', '10.02921|105.77625', 'Ninh Kiều, Cần Thơ, Việt Nam', NULL);
INSERT INTO THUC_PHAM (TP_MA, DMDVT_MA, ND_MA, DMTP_MA, TP_TEN, TP_MO_TA, TP_HINH_ANH, TP_DON_GIA, TP_SO_LUONG, TP_NGAY_BAN, TP_VI_TRI_BAN_DO, TP_DIA_CHI, TP_SUAT_BAN) VALUES (288, 1, 88, 27, 'Ớt chuông đỏ – Loại 1kg', ' Ớt chuông là loại quả có lượng vitamin C kỉ lục. Cứ 100 g ớt có chứa 120 mg vitamin C. Lượng vitamin C này gấp 2,5 lần so với cam. Thực tế, chỉ cần 50g ớt tây đã chứa 60g vitamin C tương đương với 75% lượng vitamin C cơ thể cần mỗi ngày. Ngoài vitamin C, ớt tây cũng rất giàu vitamin A. Cứ 100g ớt tây có chứa khoảng 3,5mg vitamin A, có thể cung cấp từ 15 đến 50 % lượng vitamin A cần thiết trong ngày.', 'static/images/1989bf6a-7702-4021-adca-406e8d8c4a82.jpg', 700000, 500, '2021-11-27', '10.03592|105.75738', 'Ninh Kiều, Cần Thơ, Việt Nam', NULL);
