INSERT INTO THUC_PHAM (TP_MA, DMDVT_MA, ND_MA, DMTP_MA, TP_TEN, TP_MO_TA, TP_HINH_ANH, TP_DON_GIA, TP_SO_LUONG, TP_NGAY_BAN, TP_VI_TRI_BAN_DO, TP_DIA_CHI, TP_SUAT_BAN) VALUES (293, 1, 93, 32, 'Lá hương thảo (Rosemary)', ' Lá hương thảo là một gia vị phổ biến ở nhiều nước phương Tây. Hương thảo có đặc tính cay nồng, không hề bị mất đi hương thơm trong khi nấu nướng. Khi thái nhỏ, lá hương thảo là một gia vị tuyệt vời để trộn salad, đặc biệt là với nước sốt nhẹ và giấm. Hương thảo được sử dụng trong việc chế biến các món ăn từ cá, thịt, đặc biệt là thịt gia cầm, và cho cả các loại rau củ. Loại thảo mộc này thường được dùng cho khoai tây và thích hợp cho các loại rau củ chiên với dầu oliu. Một hiệu ứng cho món thịt nướng tuyệt vời là rắc lá hương thảo lên than hồng, khi đó lá bị đốt cháy tỏa ra mùi hương thơm lừng hòa quyện vào từng thớ thịt .', 'static/images/422117f1-9d91-400a-9792-c9cb10011581.jpg', 515000, 500, '2021-11-27', '10.04939|105.78548', 'Ninh Kiều, Cần Thơ, Việt Nam', NULL);
INSERT INTO THUC_PHAM (TP_MA, DMDVT_MA, ND_MA, DMTP_MA, TP_TEN, TP_MO_TA, TP_HINH_ANH, TP_DON_GIA, TP_SO_LUONG, TP_NGAY_BAN, TP_VI_TRI_BAN_DO, TP_DIA_CHI, TP_SUAT_BAN) VALUES (294, 1, 94, 26, 'Rau Kinh giới – Loại 1kg', '', 'static/images/1adfaa0c-8de9-4aec-99e6-a3da4bdf0fbf.jpg', 560000, 500, '2021-11-27', '10.04611|105.72042', 'Ninh Kiều, Cần Thơ, Việt Nam', NULL);
INSERT INTO THUC_PHAM (TP_MA, DMDVT_MA, ND_MA, DMTP_MA, TP_TEN, TP_MO_TA, TP_HINH_ANH, TP_DON_GIA, TP_SO_LUONG, TP_NGAY_BAN, TP_VI_TRI_BAN_DO, TP_DIA_CHI, TP_SUAT_BAN) VALUES (295, 1, 95, 32, 'Lá quế Tây (Basil) – Loại 1kg', ' Húng tây là loại rau thơm ngon thường được sử dụng như một loại gia vị thêm vào các món ăn và có nhiều đặc tính chữa bệnh. Nó có tác dụng điều trị đau bụng, tiêu chảy, viêm khớp, đau họng và nhiều bệnh khác. Loại thảo dược này có chứa rất nhiều chất dinh dưỡng có lợi cho da, tóc và cơ thể. Với húng tây, có thể chế biến nhiều món ăn thơm ngon hấp dẫn như bánh mì nướng cà chua và lá húng tây, nghêu xào húng tây, cà tím rán sốt húng tây…', 'static/images/954d0b87-7031-40b8-9904-0be0858bdd1c.jpg', 211330, 500, '2021-11-27', '10.0036|105.75167', 'Ninh Kiều, Cần Thơ, Việt Nam', NULL);
INSERT INTO THUC_PHAM (TP_MA, DMDVT_MA, ND_MA, DMTP_MA, TP_TEN, TP_MO_TA, TP_HINH_ANH, TP_DON_GIA, TP_SO_LUONG, TP_NGAY_BAN, TP_VI_TRI_BAN_DO, TP_DIA_CHI, TP_SUAT_BAN) VALUES (296, 1, 96, 26, 'Húng lủi – Loại 1kg', ' Húng lũi (còn được gọi là húng láng, húng nhũi, húng lủi, húng dũi) thường được sử dụng làm rau ăn với thịt nướng, ăn gỏi, ăn với các loại rau sống khác, là gia vị được ưa chuộng. Húng lũi rất tốt cho tiêu hóa, có tác dụng ngừa ung thư, làm đẹp da và giúp hơi thở thơm mát.', 'static/images/c7cdf0fa-bd0e-4be5-8c89-fd20c1f92644.jpg', 700000, 500, '2021-11-27', '10.0243|105.75543', 'Ninh Kiều, Cần Thơ, Việt Nam', NULL);
