INSERT INTO THUC_PHAM (TP_MA, DMDVT_MA, ND_MA, DMTP_MA, TP_TEN, TP_MO_TA, TP_HINH_ANH, TP_DON_GIA, TP_SO_LUONG, TP_NGAY_BAN, TP_VI_TRI_BAN_DO, TP_DIA_CHI, TP_SUAT_BAN) VALUES (784, 1, 84, 30, 'Thịt heo sấy thượng hạng Vissan – Loại 500g', 'Lấy miếng đùi heo sấy từ tủ lạnh,&nbsp;cho vào lò làm nóng lại một chút rồi&nbsp;&nbsp;xắt thành từng lát mỏng, dọn dùng&nbsp;kèm với bánh tráng và rau sống. Món&nbsp;ăn dùng đãi khách, hẳn khách cũng&nbsp;trầm trồ, mà dùng “chữa cháy” khi&nbsp;đói lòng mà lỡ bữa, hẳn không ai có&nbsp;thể chê!&nbsp;Cũng những sớ thịt dai giòn ấy, cắt&nbsp;miếng kho với rau củ, ắt hẳn sẽ rất&nbsp;“hao cơm”. Này củ cải, này khổ qua,&nbsp;không thể thiếu ít cà rốt đỏ au hay&nbsp;&nbsp;đậu que vừa giòn vừa ngọt, món đùi&nbsp;heo sấy kho laghim là một bản tổng&nbsp;hòa từ màu sắc đến hương vị.&nbsp;Còn nếu kho với nước dừa, trứng cút,&nbsp;món ăn sẽ gợi nhớ một chút đến món&nbsp;thịt kho Tàu, nhưng độ ngọt chắc của&nbsp;miếng thịt, hẳn món ăn truyền thống&nbsp;ấy còn phải thua xa!&nbsp;&nbsp;đậu que vừa giòn vừa ngọt, món đùi&nbsp;heo sấy kho laghim là một bản tổng&nbsp;hòa từ màu sắc đến hương vị.Còn nếu kho với nước dừa, trứng cút,&nbsp;món ăn sẽ gợi nhớ một chút đến món&nbsp;thịt kho Tàu, nhưng độ ngọt chắc củamiếng thịt, hẳn món ăn truyền thống&nbsp;ấy còn phải thua xa! ', 'static/images/43c67cd4-2138-4bee-9d81-6de9c22a7a17.jpg', 211330, 500, '2021-11-27', '10.0450298|105.775282', 'Ninh Kiều, Cần Thơ, Việt Nam', NULL);
