INSERT INTO THUC_PHAM (TP_MA, DMDVT_MA, ND_MA, DMTP_MA, TP_TEN, TP_MO_TA, TP_HINH_ANH, TP_DON_GIA, TP_SO_LUONG, TP_NGAY_BAN, TP_VI_TRI_BAN_DO, TP_DIA_CHI, TP_SUAT_BAN) VALUES (791, 5, 91, 18, 'Lạp xưởng tôm Vissan – Loại hộp 200g', '• Thành phần: Thịt tôm (50%), nạc heo, mỡ heo, ruột heo khô, đường, muối i-ốt, rượu trắng, chất điều vị (621), phẩm màu tổng hợp (129). • Hướng dẫn sử dụng: Luộc chín trong nước, sau đó chiên lửa nhỏ với dầu ăn hco vàng đều. Có thể nướng trên lửa than hay trong lò nướng. • Hướng dẫn bảo quản: Bảo quản nơi khô mát, thoáng mát, tránh ánh nắng trực tiếp.', 'static/images/461615ba-bc9b-4eb0-af63-c063c1a221ec.jpg', 630000, 500, '2021-11-27', '10.03251|105.7808', 'Ninh Kiều, Cần Thơ, Việt Nam', NULL);
INSERT INTO THUC_PHAM (TP_MA, DMDVT_MA, ND_MA, DMTP_MA, TP_TEN, TP_MO_TA, TP_HINH_ANH, TP_DON_GIA, TP_SO_LUONG, TP_NGAY_BAN, TP_VI_TRI_BAN_DO, TP_DIA_CHI, TP_SUAT_BAN) VALUES (792, 5, 92, 18, 'Lạp xưởng tôm đặc biệt Vissan – Loại hộp 1kg', 'Thành phần: Thịt tôm (60%), nạc heo, mỡ heo, ruột heo khô, đường, rượu vang, muối i-ốt, chất điều vị (621), phẩm màu tổng hợp (129). Hướng dẫn sử dụng: Luộc chín trong nước, sau đó chiên lửa nhỏ với dầu ăn cho vàng đều. Có thể nướng trên lửa than hay trong lò nướng. Hướng dẫn bảo quản: Bảo quản nơi khô mát, tránh ánh nắng trực tiếp.', 'static/images/d5251aa0-e86b-4301-b5be-85433da73f1c.jpg', 260000, 500, '2021-11-27', '10.11074|105.63005', 'Ninh Kiều, Cần Thơ, Việt Nam', NULL);
