INSERT INTO THUC_PHAM (TP_MA, DMDVT_MA, ND_MA, DMTP_MA, TP_TEN, TP_MO_TA, TP_HINH_ANH, TP_DON_GIA, TP_SO_LUONG, TP_NGAY_BAN, TP_VI_TRI_BAN_DO, TP_DIA_CHI, TP_SUAT_BAN) VALUES (885, 5, 85, 17, 'Trứng cút vĩ 30', 'Trứng cút là loại thực phẩm rất tốt cho sức khỏe, giàu các chất như đồng, coban, niacin và các axit amin thiết yếu. Tyrosine là loại dưỡng chất có khả năng làm cho da khỏe mạnh. Vì thế, trứng chim cút còn được sử dụng cả trong nghành công nghiệp sản xuất mỹ phẩm. Nồng độ lecithin cao trong trứng cũng có tác dụng làm giảm lượng cholesterol trong máu. Trứng cút cũng là một loại thực phẩm có tính kháng khuẩn. Nếu bạn thường xuyên bị cảm lạnh tra tấn thì các nhà khoa học khuyên bạn nên ăn trứng cút vào mỗi sáng. Trứng cút cũng được khuyến khích dùng cho những người bị thiếu máu, nhức đầu nặng, hen phế quản, viêm dạ dày. Normalizes trong trứng cũng tốt cho huyết áp và cải thiện tiêu hóa. Ngoài ra, vỏ trứng tuy mỏng nhưng lại là một nguồn canxi dồi dào cùng 26 dưỡng chất khác như: đồng, florua, sắt, mangan, molypden, phốt pho, silic, lưu huỳnh, kẽm, silicon… Đặc biệt là có những chất có giá trị là silic và molypden – 2 chất rất cần cho các phản ứng sinh hóa của cơ thể nhưng rất nghèo trong các thực phẩm hàng ngày của chúng ta.', 'static/images/ea15abb2-6724-4b04-ba1c-7506186937b4.jpg', 190000, 500, '2021-11-27', '10.0485946|105.7700078', 'Ninh Kiều, Cần Thơ, Việt Nam', NULL);
