INSERT INTO THUC_PHAM (TP_MA, DMDVT_MA, ND_MA, DMTP_MA, TP_TEN, TP_MO_TA, TP_HINH_ANH, TP_DON_GIA, TP_SO_LUONG, TP_NGAY_BAN, TP_VI_TRI_BAN_DO, TP_DIA_CHI, TP_SUAT_BAN) VALUES (1158, 2, 58, 14, 'Má đùi gà CP – Loại 5kg', 'Má đùi Gà CP là sản phẩm của công ty CP Việt Nam. Má đùi gà được chế biến trên dây chuyền giết mổ hiện đại và được kiểm soát chặt chẽ theo tiêu chuẩn quốc tế. Có thể dùng chế biến các món ăn hằng ngày cho gia đình, liên hoan, lễ tiệc…&nbsp;Má đùi gà&nbsp;có thể dùng để chế biến các món ăn hấp dẫn, bổ dưỡng cho gia đình như:&nbsp; Má đùi gà&nbsp;rim nước dừa,&nbsp; Má đùi gà&nbsp;rán vị gừng,&nbsp; Má đùi gà&nbsp;sốt chua ngọt,&nbsp; Má đùi gà&nbsp;nướng với tương mù tạt,&nbsp; Má đùi gà&nbsp;quay,&nbsp; Má đùi gà&nbsp;nướng tẩm gia vị hay&nbsp; Má đùi gà&nbsp;áp chảo…', 'static/images/444c9cf0-6be5-48a6-b240-681ff678fe6d.jpg', 825000, 500, '2021-11-27', '10.00837|105.79014', 'Ninh Kiều, Cần Thơ, Việt Nam', NULL);
