INSERT INTO THUC_PHAM (TP_MA, DMDVT_MA, ND_MA, DMTP_MA, TP_TEN, TP_MO_TA, TP_HINH_ANH, TP_DON_GIA, TP_SO_LUONG, TP_NGAY_BAN, TP_VI_TRI_BAN_DO, TP_DIA_CHI, TP_SUAT_BAN) VALUES (1768, 1, 68, 31, 'Bò 2 lát 3 bông mai Vissan – Hộp 150g', 'Trọng lượng: 150grDạng thành phẩm: hộp thiếcSản phẩm đạt chất lượng vệ sinh an toàn thực phẩmThực phẩm Vissan&nbsp;Sản phẩm của Việt NamNhững khi bận rộn, chị em phụ nữ chúng ta có thể thêm thịt hộp vào thực đơn vì món ăn thịt hộp giờ đây rất đa dạng và phong phú, lại rất tiện lợi khi chế biến, không tốn nhiều thời gian.Thịt hộp bò hai lát làm từ thịt heo tươi còn nóng được ướp cùng với gia vị cho ra món. Bò hai lát thơm ngon đảm bảo vệ sinh, bò hai lát có thể dùng làm món ăn trong mâm cơm hàng ngày của bạn, ăn kèm với bánh mì, cho vào tô mì gói nóng hổi, hoặc ăn cùng bún tươi rất ngon. Thích hợp cho những người bận rộn công việc. Món ăn này cung cấp đủ canxi cho bữa ăn và còn rất ngon miệng nữa. Hãy luôn để thịt hộp bò hai lát trong nhà bạn nhé.', 'static/images/3e151607-6f4c-4d41-b81e-65749a9caa56.jpg', 211330, 500, '2021-11-27', '10.0354938|105.7716347', 'Ninh Kiều, Cần Thơ, Việt Nam', NULL);
INSERT INTO THUC_PHAM (TP_MA, DMDVT_MA, ND_MA, DMTP_MA, TP_TEN, TP_MO_TA, TP_HINH_ANH, TP_DON_GIA, TP_SO_LUONG, TP_NGAY_BAN, TP_VI_TRI_BAN_DO, TP_DIA_CHI, TP_SUAT_BAN) VALUES (1769, 5, 69, 25, 'Nấm mối đen tươi đệ nhị – Gói 250g', 'Nấm mối đen tươi đệ nhất được nuôi trồng theo công nghệ khép kín 100% và chuẩn sạch hữu cơ. Nấm mối đen tươi được thu hoặc và xử lý bảo quản trong ngày để bảo đảm chất lượng được tốt nhất.Nấm mối đen có vị thanh ngọt, giòn, và chứa nhiều chất dinh dưỡng giá trị cao giúp: tăng cường sức đề kháng, ngăn ngừa ung thư, chống lão hóa, hỗ trợ tiêu hóa, giải độc và tăng cường chức năng gan…và còn nhiều chức năng nữa.Nấm mối đen tươi đệ nhất rất dễ chế biến và làm được với rất nhiều món ngon: súp nấm mối đen, trứng chiên nấm mối đen, bánh xèo chay nấm mối đen, nấm mối đen nướng, lẩu,…thậm chí là nấu mì gói với nấm mối đen tươi cũng rất ngon.Thực Phẩm Nhanh đang nuôi trồng và phân phối 3 loại nấm mối đen tươi tiêu chuẩn chất lượng cao bao gồm:– Nấm mối đen tươi đệ nhất: Là loại nấm mối được nuôi trồng khép kín, đạt chất lượng tốt nhất, kích thước đạt chuẩn lớn nhất và được cung cấp riêng cho các nhà hàng, khách sạn lớn tại TPHCM.– Nấm mối đen tươi đệ nhị: Loại nấm này cũng bảo đảm chất lượng 100% như nấm mối đen đệ nhất, tuy nhiên kích thước sẽ nhỏ hơn so với nấm mối đen đệ nhất.– Nấm mối đen tươi đệ tam: Nhằm mang đến những trải nghiệm sử dụng tốt nhất cho tất cả các phân khúc khách hàng của Thực Phẩm Nhanh.', 'static/images/842f6eb9-e713-4be2-8264-5864fd12e7a8.jpg', 720000, 500, '2021-11-27', '10.03102|105.77854', 'Ninh Kiều, Cần Thơ, Việt Nam', NULL);
