INSERT INTO THUC_PHAM (TP_MA, DMDVT_MA, ND_MA, DMTP_MA, TP_TEN, TP_MO_TA, TP_HINH_ANH, TP_DON_GIA, TP_SO_LUONG, TP_NGAY_BAN, TP_VI_TRI_BAN_DO, TP_DIA_CHI, TP_SUAT_BAN) VALUES (1, 1, 1, 39, 'Bắp cải trắng – Loại 1kg', 'Cải trắng có vị ngọt, tính mát, có tác dụng hạ khí, thanh nhiệt, chứa nhiều vitamin A, B, C, E. Hàm lượng nguyên tố vi lượng kẽm cao hơn cả thịt, cá. Cải thảo nấu chín chứa nhiều vitamin A, C, K, B2, B6, calcium, sắt, mangan, folat, cũng như nhiều thành phần hoạt chất có ảnh hưởng tốt đối với sức khỏe. Cải thảo có thể dùng nấu canh ăn như các loại rau cải khác, cũng có thể ăn sống, muối chua, làm nộm như rau xà lách, nấu lẩu, xào… Khi chế biến cải thảo, bạn không nên nấu chín quá sẽ làm cải mất độ ngon, giòn và các vitamin dễ tan ở nhiệt độ cao.', 'static/images/2bab2c40-ad45-43af-a019-c45baf6afa44.jpg', , 500, '2021-11-27', '10.03|105.77597', 'Ninh Kiều, Cần Thơ, Việt Nam', NULL)
INSERT INTO THUC_PHAM (TP_MA, DMDVT_MA, ND_MA, DMTP_MA, TP_TEN, TP_MO_TA, TP_HINH_ANH, TP_DON_GIA, TP_SO_LUONG, TP_NGAY_BAN, TP_VI_TRI_BAN_DO, TP_DIA_CHI, TP_SUAT_BAN) VALUES (2, 1, 2, 25, 'Dưa leo – Loại 1kg', '', 'static/images/f1793561-2f5b-49ec-92a7-dfa087a0ad94.jpg', , 500, '2021-11-27', '10.0095|105.79059', 'Ninh Kiều, Cần Thơ, Việt Nam', NULL)
INSERT INTO THUC_PHAM (TP_MA, DMDVT_MA, ND_MA, DMTP_MA, TP_TEN, TP_MO_TA, TP_HINH_ANH, TP_DON_GIA, TP_SO_LUONG, TP_NGAY_BAN, TP_VI_TRI_BAN_DO, TP_DIA_CHI, TP_SUAT_BAN) VALUES (3, 1, 3, 25, 'Bí đỏ hồ lô – Loại 1kg', 'Bí đỏ là nguồn cung cấp dồi dào vitamin A, đóng vai trò quan trọng cho thị giác, tăng trưởng xương và sự sinh sản, điều hòa hệ miễn dịch và bảo vệ cho da. Bí đỏ được coi là món ăn bổ não, trị suy nhược thần kinh, trẻ em chậm phát triển về trí óc, và có tính nhuận trường. Ngoài tỷ lệ chất xơ và sắt khá cao, bí đỏ còn mang lại vitamin C, acid folic, magiê, kali và nhiều nguyên tố vi lượng khác. Đây cũng là thực phẩm cần cho những ai sợ mập vì dù có hàm lượng protein cao nhưng lại không chứa chất béo.', 'static/images/a4e8d046-754b-4bc9-bf6e-0c8f4d63725a.jpg', , 500, '2021-11-27', '10.03655|105.75801', 'Ninh Kiều, Cần Thơ, Việt Nam', NULL)